INSERT INTO `aj_house_type` VALUES ('25', '<p><span style=\"font-size: 14px;\">5#户型分布：</span></p><p><br/></p><h3 class=\"fl\" style=\"outline: none; margin: 0px; padding: 0px; font-size: 14px; float: left; display: inline; width: 70px;\">户型解析：</h3><p class=\"fl\" id=\"jiexi\" style=\"outline: none; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; float: left; display: inline; width: 330px; height: 163px; overflow-x: hidden; overflow-y: auto;\">主卧或客厅方正，其他空间不方正；卫生间没有对外窗户；卧室作为休息区域，门朝向客厅或餐厅时，私密性一般；卫生间门朝向主要活动区域；。客厅、卧室、卫生间和厨房等主要功能间的尺寸以及比例适中，有利于采光和通风，居住起来舒适、方便；公摊高于15%且低于25%，符合住宅公摊正常范</p>', '2018-01-06 17:28:32.552000', '2', '1', '1-4#楼户型介绍', '125', '9', '', '/static/upload/image/20180106/1515230805490005640.jpg', '/static/upload/image/20180106/1515230816224036866.jpg|/static/upload/image/20180106/1515230820990059787.jpg', '3', '2', '1', '12', '0');
INSERT INTO `aj_house_type` VALUES ('26', '<p><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif;\">B户型分布于：2#楼一单元，二单元。<br/></span></p><p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">面积：83㎡--85㎡，赠送10㎡.</span></p><p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">●超大观景阳台视野宽阔，可欣赏园林景观及整个北海全景；</span></p><p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">●三面采光，南北通透，无论是独立性，私密性都非常好l；</span></p><p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">●明厨明卫通风透气效果俱佳，赠送面积多，利用空间大；</span></p><p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">●功能分区，双卫设计，清晰使用灵活，实用性强。</span></p>', '2018-01-30 11:02:33.136000', '1', '1', 'B户型', '83', '5', '2.95米', '/static/upload/image/20180130/1517281011808054255.jpg', '/static/upload/image/20171119/1511086236709064430.jpg|/static/upload/image/20171119/1511086236834039625.jpg|/static/upload/image/20171119/1511086236865068362.jpg', '3', '2', '2', '5', '0');
INSERT INTO `aj_house_type` VALUES ('27', '<p><span style=\"font-size: 18px;\">A户型分布于：2#楼一单元，二单元。</span></p><p><span style=\"font-size: 18px;\">面积：96㎡，赠送9㎡</span></p><p><span style=\"font-size: 18px;\">●方正朝南设计巧妙，客厅与观景阳台相连通透明亮，充分享受清风的爱抚；</span></p><p><span style=\"font-size: 18px;\">●卧室均有全景窗眺望银滩海景及俯瞰园林胜景，生活与自然相得益彰；</span></p><p><span style=\"font-size: 18px;\">●阳台、主次卧均可看到银滩公园，绵延着24公里最核心的观景位置；</span></p><p><span style=\"font-size: 18px;\">●明厨明卫通风透气，超大的观景阳台视野宽阔。</span></p>', '2018-01-30 11:15:12.292000', '1', '1', 'A户型', '96', '2', '2.95', '/static/upload/image/20180130/1517281755573008116.jpg', '/static/upload/image/20180130/1517281780120022410.jpg|/static/upload/image/20180130/1517281783167012512.jpg|/static/upload/image/20180130/1517281785120037339.jpg', '3', '2', '2', '5', '0');
INSERT INTO `aj_house_type` VALUES ('28', '<p><span style=\"font-size: 18px;\">C户型分布于：2#楼一单元，二单元。</span></p><p><span style=\"font-size: 18px;\">面积：67㎡，赠送6.5㎡</span></p><p><span style=\"font-size: 18px;\">●朝南更能沐浴阳光，通风舒畅人心喜悦，独揽绝美风光；</span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●赠送面积多，买一房得两房，性价比高、升值空间大；</span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●紧凑舒适，功能齐全且分明，空间利用及实用性高；</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●明厨明卫，畅享舒心烹饪，生活区、居住区合理分明。</span></span></span></span></p>', '2018-01-30 11:22:30.698000', '1', '1', 'C户型', '67', '5', '2.95', '/static/upload/image/20180130/1517282272198037979.jpg', '/static/upload/image/20180130/1517282287542098041.jpg|/static/upload/image/20180130/1517282291245047320.jpg|/static/upload/image/20180130/1517282293354089229.jpg', '2', '2', '1', '5', '0');
INSERT INTO `aj_house_type` VALUES ('29', '<p><span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, 微软雅黑, &quot;Microsoft Yahei&quot;, &quot;雅黑\\9 &quot;, Arial; font-size: 14px; line-height: 26px; widows: 1;\">舒适两房，南北通透，动静分离。科学区分，厨房、生活阳台相连，洁污分区，干湿分离。各个空间户型方正，后期空间利用率高；全明通透户型，居住舒适度较高，整个空间采光充足，利于后期居住；卫生间朝向餐厅产生的气味及细菌对餐厅影响较大。</span></p>', '2018-01-16 17:36:11.873000', '2', '1', '73平米两房', '73', '2', '32', '/static/upload/image/20171119/1511090687409097190.jpg', '/static/upload/image/20171119/1511090778191019481.jpg|/static/upload/image/20171119/1511090778206077772.jpg|/static/upload/image/20171119/1511090778238091299.jpg|/static/upload/image/20171119/1511090778238075053.jpg|/static/upload/image/20171119/1511090778253057645.jpg|/static/upload/image/20171119/1511090780003099658.jpg', '2', '2', '1', '1', '0');
