INSERT INTO `aj_house_type` VALUES ('125', '<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">E户型位于2#楼二单元</span></p><p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">面积：70㎡，赠送7㎡</span></p><p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">●方正实用，合理布局，朝南更能充分沐浴阳光；</span></p><p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">●双阳台设计，合理规划空间，随意切换设计想法，尽享海风徐徐；</span></p><p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">●遥望北海最美冠岭山庄，将海滩与建筑完美交融；</span></p><p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">●紧凑精巧且实用性强，布局合理，干湿分离。</span></p>', '2018-01-30 11:40:17.433000', '2', '1', 'E户型', '70', '2', '', '/static/upload/image/20180130/1517283366229006958.jpg', '/static/upload/image/20180130/1517283390933080762.jpg|/static/upload/image/20180130/1517283380839022214.jpg|/static/upload/image/20180130/1517283385589073638.jpg', '2', '2', '1', '5', '0');
INSERT INTO `aj_house_type` VALUES ('126', '<p>B/C户型分布于：1#/2#/5#楼</p><p>面积：67m²+12m²</p><p>●拓展面积高达28%，朝南、看海；</p><p>●1.8米宽敞景观阳台，享大海美景；</p><p style=\"white-space: normal;\">●小户型大利用，拓展价值满足不同阶段的居住需求；</p><p style=\"white-space: normal;\">●宽敞客厅与景观阳台一气呵成，面朝大海春暖花开；</p><p style=\"white-space: normal;\">●明厨设计与餐厅毗邻，第一时间品尝美味人生。</p>', '2018-02-01 14:01:10.283000', '1', '1', 'B/C户型', '67', '2', '', '/static/upload/image/20180201/1517464460908013978.jpg', '/static/upload/image/20180201/1517464466486018259.jpg|/static/upload/image/20180201/1517464474127029486.jpg|/static/upload/image/20180201/1517464476424030588.jpg|/static/upload/image/20180201/1517464479611052694.jpg', '2', '2', '1', '47', '1');
INSERT INTO `aj_house_type` VALUES ('127', '<p>C迷你别墅</p><p>面积：105m²+120m²，0公摊。</p><p>户型品鉴：</p><p><strong>●一线迷你别墅，功能齐备，轻松揽入</strong></p><p>●负一层采光充足，可做娱乐室、休憩室等多功能空间；</p><p>●一层超大空间客厅、餐厅，全家人的欢享天地户户带私家观景泳池，畅游江海之上，更属稀缺；</p><p>●二层4.4米开间主卧，套房设计，尊享私密空间衣帽间、浴室、观景露台俱全，推窗，风光无限；</p><p>●三层卧室带三露台，270°观景界面，养花、品茗随心布置北海王牌景点，一墅藏纳；生活品位境界，自此升华。</p>', '2018-02-01 19:32:41.197000', '1', '1', 'C迷你别墅', '105', '9', '', '/static/upload/image/20180201/1517484109916011002.jpg', '/static/upload/image/20180201/1517484130400087121.jpg|/static/upload/image/20180201/1517484133260047663.jpg|/static/upload/image/20180201/1517484180588081920.jpg|/static/upload/image/20180201/1517484147932036723.jpg|/static/upload/image/20180201/1517484153103098914.jpg|/static/upload/image/20180201/1517484189494074063.jpg', '4', '2', '2', '38', '5');
