INSERT INTO `aj_house_type` VALUES ('135', '<p><span style=\"font-size: 18px;\">A1&amp;A3户型分布于1#、2#、3#楼<br/></span></p><p><span style=\"font-size: 18px;\">户型解析：</span></p><p><span style=\"font-size: 18px;\">●南北通透的全明户型，有效衔接室内外空间。</span></p><p><span style=\"font-size: 18px;\"></span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px;\">●双卧室飘窗，静享滨海阳光与美景。</span></p><p><span style=\"font-size: 18px;\"></span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px;\">●独立景观客厅，朝南大阳台，正对园博园。</span></p><p><span style=\"font-size: 18px;\"></span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px;\">●与餐厅衔接的全明L型厨房，给你增添烹饪与美食的居家乐趣。</span></p><p><span style=\"font-size: 18px;\"><br/></span></p><p><span style=\"font-size: 18px;\"><br/></span><br/></p><p><br/></p>', '2018-02-07 23:10:47.536000', '1', '1', 'A1&A3户型', '92', '9', '', '/static/upload/image/20180207/1518014455864047750.jpg', '/static/upload/image/20180207/1518014460692012167.jpg', '2', '2', '1', '65', '1');
INSERT INTO `aj_house_type` VALUES ('136', '<p><span style=\"font-size: 18px;\">A2户型位于2#楼</span><br/></p><p><span style=\"font-size: 18px;\">户型解析：</span></p><p><span style=\"font-size: 18px;\">●南北通透的全户型，有效衔接室内外空间。</span></p><p><span style=\"font-size: 18px;\">●双卧室飘窗，静享滨海阳光与美景。</span></p><p><span style=\"font-size: 18px;\">●独立景观客厅，朝南大阳台，面对园博园，令居者享受无尽风光。</span></p><p><span style=\"font-size: 18px;\">●而难能可贵的是103m²的面积，承载了三房两厅两卫，在北海仅此一寓。</span></p><p><span style=\"font-size: 18px;\"><br/></span></p>', '2018-02-07 22:52:27.520000', '1', '1', 'A2户型', '103', '9', '', '/static/upload/image/20180207/1518014861036088781.jpg', '/static/upload/image/20180207/1518014864786039687.jpg', '3', '2', '2', '65', '1');
INSERT INTO `aj_house_type` VALUES ('137', '<p><span style=\"font-size: 18px;\">B2户型分布于S8#、S9#楼</span><br/></p><p><span style=\"font-size: 18px;\">户型解析：</span></p><p><span style=\"font-size: 18px;\">●我们把63m²的酒店公寓做到了绝佳的境地，使它除了拥有南北朝向和二面采光外，还使它如同标准宅第那样拥有独立的客厅、卧室、书房、餐厅、厨房、明卫和朝南的阳台，北海仅此一家。</span></p><p><span style=\"font-size: 18px;\"><br/></span></p>', '2018-02-07 22:59:12.223000', '1', '1', 'B2户型', '63', '9', '', '/static/upload/image/20180207/1518015290457039034.jpg', '/static/upload/image/20180207/1518015296786089552.jpg', '1', '2', '1', '65', '1');
