INSERT INTO `aj_house_type` VALUES ('182', '<p><span style=\"font-size: 18px;\">B12户型位于二期B栋</span></p><p><span style=\"font-size: 18px;\">户型解析：</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\">●各个空间户型方正，后期空间利用率高；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\">●整个空间采光很好，卫生间位置合理，使用起来动线比较合理；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\">●厨房位于门口，方便使用和油烟的排出；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\">●卧室厨卫等各个功能区的面积大小都比较合理，居住体验便利，整体舒适度高；</span></p>', '2018-02-11 22:56:04.536000', '1', '1', 'B12户型', '36', '2', '', '/static/upload/image/20180211/1518360869708098800.jpg', '', '1', '1', '1', '68', '1');
INSERT INTO `aj_house_type` VALUES ('183', '<p><span style=\"font-size: 18px;\">01、02户型分布于1栋1单元、2单元</span></p><p><span style=\"font-size: 18px;\">面积：1单01户型、2单02户型117m²+17m²；1单02户型、2单01户型114m²+17m²</span></p><p><span style=\"font-size: 18px;\">户型解析：</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-family: arial, helvetica, sans-serif; font-size: 18px;\">●除主卧和客厅之外的其他空间不方正；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-family: arial, helvetica, sans-serif; font-size: 18px;\">●全明户型，各部分空间均有窗，可保证整体空间采光和通风，居住舒适度好；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-family: arial, helvetica, sans-serif; font-size: 18px;\">●厨房门朝向客厅，装修时要侧重考虑除油烟的措施；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-family: arial, helvetica, sans-serif; font-size: 18px;\">●次卧的面宽进深比不在标准范围内，装修时的空间改造，可提升对次卧的利用率；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-family: arial, helvetica, sans-serif; font-size: 18px;\">●公摊高于15%且低于25%，符合住宅公摊正常范围</span><span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, 微软雅黑, &#39;Microsoft Yahei&#39;, &#39;雅黑\\9&#39;, Arial; font-size: 14px; line-height: 26px;\">。</span></p>', '2018-02-12 21:04:36.286000', '1', '1', '1#1、2单元01、02户型', '117', '9', '', '/static/upload/image/20180212/1518423646349064314.jpg', '', '4', '2', '2', '56', '1');
INSERT INTO `aj_house_type` VALUES ('184', '<p><span style=\"font-size: 18px;\">01、02户型位于二栋1单元<br/></span></p><p><span style=\"font-size: 18px;\">面积：01户型120m²+<span style=\"font-size: 18px;\">14m²；</span>02户型面积109m²+14m²。</span></p><p><span style=\"font-size: 18px;\">户型解析：</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\">●主卧或客厅方正，其他空间不方正；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\">●户型不是南北通透，后期装修时可以考虑加装纱门改善通风；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\">●卫生间门朝向主要活动区域；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\">●次卧的面宽进深比不在标准范围内，装修时的空间改造，可提升对次卧的利用率；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\">●公摊高于15%且低于25%，符合住宅公摊正常范围。</span></p>', '2018-02-12 21:05:37.599000', '1', '1', '2#1单元01、02户型', '120', '1', '', '/static/upload/image/20180212/1518424094677090956.jpg', '', '3', '2', '2', '56', '2');
INSERT INTO `aj_house_type` VALUES ('185', '<p><span style=\"font-size: 18px;\">01户型位于二栋2单元</span></p><p><span style=\"font-size: 18px;\">面积：80m²+10m²</span></p><p><span style=\"font-size: 18px;\">户型解析：</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-family: arial, helvetica, sans-serif; font-size: 18px;\">●次要空间不方正；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-family: arial, helvetica, sans-serif; font-size: 18px;\">●户型不是南北通透，后期装修时可以考虑加装纱门改善通风；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-family: arial, helvetica, sans-serif; font-size: 18px;\">●卫生间门朝向主要活动区域，私密性一般；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-family: arial, helvetica, sans-serif; font-size: 18px;\">●次卧的面宽进深比不在标准范围内，要适当缩放家具比例，让空间看起来更开阔；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-family: arial, helvetica, sans-serif; font-size: 18px;\">●公摊高于15%且低于25%，符合住宅公摊正常范围。</span></p>', '2018-02-12 21:06:37.490000', '1', '1', '2#2单元01户型', '80', '1', '', '/static/upload/image/20180212/1518425458818093596.jpg', '', '3', '2', '1', '56', '1');
