INSERT INTO `aj_house_type` VALUES ('188', '<p><span style=\"font-size: 18px;\">03、04户型位于3栋1单元</span></p><p><span style=\"font-size: 18px;\">面积：03户型94m²+8m²，04户型84m²+8m²。</span></p><p><span style=\"font-size: 18px;\">户型解析：</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-family: arial, helvetica, sans-serif; font-size: 18px;\">●各个空间户型方正，方便室内家具布置；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-family: arial, helvetica, sans-serif; font-size: 18px;\">●南北不通透的户型，后期居住时常开门通风换气即可；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-family: arial, helvetica, sans-serif; font-size: 18px;\">●卫生间门朝向主要活动区域；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-family: arial, helvetica, sans-serif; font-size: 18px;\">●卫生间空间不算开阔，装修时要侧重实现其功能性；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-family: arial, helvetica, sans-serif; font-size: 18px;\">●公摊高于15%且低于25%，符合住宅公摊正常范围。</span></p>', '2018-02-12 21:07:46.943000', '1', '1', '3#1单元03、04户型', '94', '2', '', '/static/upload/image/20180212/1518437489099004372.jpg', '', '2', '2', '1', '56', '1');
