INSERT INTO `aj_house_type` VALUES ('242', '<p><span style=\"font-size: 18px;\">东院01户型位于6#楼</span><br/></p><p><span style=\"font-size: 18px;\">户型解析：</span></p><p><span style=\"font-size: 18px;\">★北方合院形制</span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●背靠山墙，三面抱海，明堂开阔，稳如泰山，传承千年人居理念；</span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●独门独院，室内动线流畅，餐厅、客厅、卧室动线互不干扰；</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●室内空间布局极具韵律感，既藏风纳气，又灵活百变、阔朗实用；</span></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●南侧设大阳台，中置开放式小书房，奢享精缩版的墅式庭院生活。</span></span></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><br/></span></span></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">★银滩世幕海景</span></span></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●纯板式建筑，户户270°瞰海，将158米外银滩巨幕海景引入室内；</span></span></span></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●落地景观窗、约14.4m²大阳台，在客厅与卧室一起赏花听海。</span></span></span></span></span></span></span></p>', '2018-02-19 18:04:54.131000', '1', '1', '东院01户型', '83', '9', '', '/static/upload/image/20180219/1519031038865060497.jpg', '/static/upload/image/20180219/1519032047990082822.jpg|/static/upload/image/20180219/1519034677146077327.jpg|/static/upload/image/20180219/1519032045553054673.jpg|/static/upload/image/20180219/1519032050771046977.jpg|/static/upload/image/20180219/1519032056115010102.jpg|/static/upload/image/20180219/1519034632990043084.jpg', '3', '2', '1', '4', '1');
INSERT INTO `aj_house_type` VALUES ('243', '<p><span style=\"font-size: 18px;\">东院2+3户型位于6#楼</span><br/></p><p><span style=\"font-size: 18px;\">户型解析：</span></p><p><span style=\"font-size: 18px;\">★北方合院形制</span></p><p><span style=\"font-size: 18px;\">●空间合围，北侧入户，独门独院，传承北方合院的千年居住理念；</span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●微错式开间设计带来灵动空间，餐厅、客厅、卧室动线互不干扰；</span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●贯通式中庭走廊，带来北部湾清新的海洋季风，以及良好的采光。</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><br/></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">★江南园林精髓</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●中置式花园约52m²，与各房间相互渗透，一院天地迎得四水归堂；</span></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●北侧两入户花园，南侧两瞰海大阳台，奢享前庭后院的墅式生活；</span></span></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●云端泡池、会客平台、枯山水堆景，深谙江南小桥流水的园林意境。</span></span></span></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><br/></span></span></span></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">★银