INSERT INTO `aj_house_type` VALUES ('244', '<p><span style=\"font-size: 18px;\">西院6+7户型位于9#、10#楼</span><br/></p><p><span style=\"font-size: 18px;\">户型解析：</span></p><p><span style=\"font-size: 18px;\">★针对海洋湿润季风气候，独有南北对称开间设计，满足良好采同时打造自然新风系统，冬季温暖夏季更清凉，干爽除湿更舒适；</span></p><p><span style=\"font-size: 18px;\">★独创6米瞰海大宽厅，打造摩天临云会所，以非凡气度睥睨壮阔海景，巅峰人生从这刻开启。</span></p><p><span style=\"font-size: 18px;\">●离海158米，盛放在银潍上的空中花园平墅，户户南向瞰海；</span></p><p><span style=\"font-size: 18px;\">●全新住家理念打造N房+空中花园户型，40%超高拓展，得房率高；</span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●花香中享受阳光与大海，是高层海景洋房与别墅式体验的完美融合；</span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●东南亚风情人居理念，推崇舒适、休闲、文化的生活方式，独有临云天池设计，在云端之上口味异国情调。</span></span></span></p>', '2018-02-19 18:00:50.443000', '1', '1', '西院6+7户型', '253', '9', '', '/static/upload/image/20180219/1519033152334016382.jpg', '/static/upload/image/20180219/1519034339318028511.jpg|/static/upload/image/20180219/1519034344584065550.jpg|/static/upload/image/20180219/1519034362334087802.jpg|/static/upload/image/20180219/1519034379474060440.jpg|/static/upload/image/20180219/1519034394178057868.jpg|/static/upload/image/20180219/1519034404803028464.jpg', '4', '2', '2', '4', '4');
