INSERT INTO `aj_house_type` VALUES ('261', '<p><span style=\"font-size: 18px;\">R栋R5户型解析：</span><br/></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\">●各个空间户型方正，后期空间利用率高；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 18px; line-height: 26px;\">●</span>全明户型，各部分空间均有窗，可保证整体空间采光和通风，居住舒适度好；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 18px; line-height: 26px;\">●</span>卫生间门朝向主要活动区域，私密性一般；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 18px; line-height: 26px;\">●</span>厨房门朝向客厅，能加装门即可规避油烟对客厅的影响；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 18px; line-height: 26px;\">●</span>客厅、卧室、卫生间和厨房等主要功能间的尺寸以及比例适中，有利于采光和通风，居住起来舒适、方便；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 18px; line-height: 26px;\">●</span>公摊高于15%且低于25%，符合住宅公摊正常范围。</span></p>', '2018-02-20 22:29:26.099000', '1', '1', 'R栋R5户型', '94', '2', '', '/static/upload/image/20180220/1519136488865040594.jpg', '', '2', '2', '1', '62', '1');
INSERT INTO `aj_house_type` VALUES ('262', '<p><span style=\"font-size: 18px;\">R栋R4户型解析：</span><br/></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\">●除主卧和客厅之外的其他空间不方正；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 18px; line-height: 26px;\">●</span>户型不是南北通透，整体空间无穿堂风；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 18px; line-height: 26px;\">●</span>厨房门朝向客厅，装修时要侧重考虑除油烟的措施；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 18px; line-height: 26px;\">●</span>卧室厨卫等各个功能区的面积大小都比较合理，居住体验便利，整体舒适度高；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 18px; line-height: 26px;\">●</span>公摊高于15%且低于25%，符合住宅公摊正常范围。</span></p>', '2018-02-20 22:26:25.037000', '1', '1', 'R栋R4户型', '128', '9', '', '/static/upload/image/20180220/1519136660521025503.jpg', '', '3', '2', '2', '62', '1');
INSERT INTO `aj_house_type` VALUES ('263', '<p><span style=\"font-size: 18px;\">R栋R10户型解析：</span><br/></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\">●各个空间户型方正，方便室内家具布置；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 18px; line-height: 26px;\">●</span>卫生间如没有窗子，可加管道通风，但是相对来说卫生间有窗户是好的情况，利于排湿，不会使湿气进到室内；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 18px; line-height: 26px;\">●</span>卧室门朝向客厅或餐厅；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 18px; line-height: 26px;\">●</span>卧室厨卫等各个功能区的面积大小都比较合理，居住体验便利，整体舒适度高；</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 26px; font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 18px; line-height: 26px;\">●</span>公摊高于15%且低于25%，符合住宅公摊正常范围。</span></p>', '2018-02-20 22:28:49.365000', '1', '1', 'R栋R10户型', '130', '9', '', '/static/upload/image/20180220/1519136809334042528.jpg', '', '3', '2', '2', '62', '1');
