INSERT INTO `aj_house_type` VALUES ('288', '<p><span style=\"font-size: 18px;\"><strong>户型解析：</strong></span><br/></p><p><span style=\"font-size: 18px;\">●各个空间户型方正，方便室内家具布置；<strong><br/></strong></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●全明户型，各部分空间均有窗，可保证整体空间采光和通风，居住舒适度好；</span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●整体空间布局合理，能够保证动静分离和居室通风，方便使用；</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●客厅、卧室、卫生间和厨房等主要功能间的尺寸以及比例适中，有利于采光和通风，居住起来舒适、方便；</span></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●公摊高于15%且低于25%，符合住宅公摊正常范围。</span></span></span></span></span></p><p style=\"position: absolute; width: 1px; height: 1px; overflow: hidden; left: -1000px; white-space: nowrap; top: 59px;\"><span style=\"font-size: 18px; white-space: normal;\">●</span></p><p style=\"position: absolute; width: 1px; height: 1px; overflow: hidden; left: -1000px; white-space: nowrap; top: 59px;\"><span style=\"font-size: 18px; white-space: normal;\"><br/></span></p>', '2018-03-16 21:41:09.758000', '1', '1', '二期6#01、06户型', '84', '2', '', '/static/upload/image/20180316/1521207428445038253.jpg', '', '2', '2', '1', '99', '1');
