INSERT INTO `aj_news` VALUES ('58', '<p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 18px;\">【爱家房产网】据北海市房地产交易中心官网发布的《2月份房产交易》的数据显示，2018年2月份北海房地产市场新建商品住宅交易网签全线下降，2月份，北海市办理新建商品房交易（网签）2649宗，环比下降22.59%，同比下降5.09%；交易面积21.14万㎡，环比下降23.49%，同比下降10.91%。其中，新建商品住房交易2375宗，环比下降24.07%，同比下降12.33%；住宅交易面积20.01万平方米，环比下降24.92%，同比下降14.30%。</span></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"/static/upload/image/20180316/1521205766242063976.jpg\" img_width=\"399\" img_height=\"189\" alt=\"北海房地产市场2月份新房网签2649宗 环比下降22.59%\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/static/upload/image/20180316/1521205847164031448.jpg\" title=\"1521205847164031448.jpg\" alt=\"6ed4000394e46e6c9363_副本.jpg\"/></p><p style=\"-webkit-tap-highlight-color: transp