INSERT INTO `aj_premise` VALUES ('15', '<p><span style=\"font-size: 18px;\">2018-02-05&nbsp;11:07:03</span></p><p><span style=\"font-size: 18px;\"><strong>【项目简介】</strong>圣景龙湾位于银滩新区中心广东路上，汇聚城市优质资源归一席。路网纵贯南北，横畅东西，交通便捷。北180亩生态公园，南向800米处稀缺银滩海景。片区高端住宅社区云集，机关幼儿园、大学城、星级酒店、海鲜城、高级会所、天然海滨浴场、万达广场、区域全系配套满足一切生活刚需。</span></p><p><span style=\"font-size: 18px;\"><strong><br/></strong></span></p><p><span style=\"font-size: 18px;\"><strong>【周边设施】</strong></span></p><p><span style=\"font-size: 18px;\"><strong>【交通】</strong>乘坐17路公交车到滨海路口站下车，向南走900米，即可以到达小区。5分钟车程到海边银滩，10分钟车程到高铁站，15分钟车程到国际客运港码头，20-2分钟到福成机场。</span></p><p><span style=\"font-size: 18px;\"><strong><br/></strong></span></p><p><span style=\"font-size: 18px;\"><strong>【内部配套】</strong>2000㎡花园广场，望月亭，观景木平台。</span></p><p><span style=\"font-size: 18px;\"><strong><br/></strong></span></p><p><span style=\"font-size: 18px;\"><strong>【楼层】</strong>高为29-32层</span></p>', '2018-03-14 01:25:08.895000', '1', '1', '圣景龙湾', '北海市银海区圣景龙湾', '64.5', '8000.00', '180000', '北海融富海湾房地产开发有限公司', '9', '新房2018-022', '3.5', '34%', '20', '【预售圣景龙湾二期3#层高32层，两梯四户】【户型：3#01、02户型（2+1房）88㎡送20㎡，3#03、04户型（2+1房）81㎡送20㎡】【2018年月开盘，2019年9月精装交房】【产权70年】', '877', '/static/upload/image/20180314/1520961895098025357_wt.jpg|/static/upload/image/20180125/1516870091417070037_wt.jpg|/static/upload/image/20180125/1516870103339064364_wt.jpg|/static/upload/image/20180125/1516870135855058968_wt.jpg|/static/upload/image/20180125/1516870140073047819_wt.jpg|/static/upload/image/20180125/1516870148370095503_wt.jpg|/static/upload/image/20180125/1516870174777001934_wt.jpg|/static/upload/image/20180125/1516870177370059748_wt.jpg|/static/upload/image/20180125/1516870179683002413_wt.jpg|/static/upload/image/20180125/1516870182105079915_wt.jpg|/static/upload/image/20180125/1516870208683078522_wt.jpg|/static/upload/image/20180125/1516870214605082131_wt.jpg|/static/upload/image/20180125/1516870217605097702_wt.jpg|/static/upload/image/20180125/1516870220761044162_wt.jpg|/static/upload/image/20180125/1