INSERT INTO `aj_premise` VALUES ('16', '<p><span style=\"font-size: 18px;\">2018-02-06&nbsp;23:29:34</span><br/></p><p><strong><span style=\"font-size: 18px;\">【项目简介】</span></strong></p><p><strong><span style=\"font-size: 18px;\">【项目位于】</span></strong><span style=\"font-size: 18px;\">北海市银海区银滩大道5号，总用地面积约49.52亩，项目建筑面积129859m²、容积率3.39、建筑密度16.68%、绿地率约34.72%的滨海社区。昊海梧桐项目由海归建筑大师担纲，采用“蝶式”设计，具有现代感；并配置了室外恒温泳池及私属会所；户型设计秉承南北通透、明厨、明卫的宜居理念。昊海梧桐目前的生活配套以北背岭以及广东南路的为主。项目地处银滩风景区核心。</span></p><p><strong><span style=\"font-size: 18px;\"><br/></span></strong></p><p><strong><span style=\"font-size: 18px;\">【周边设施】</span></strong></p><p><strong><span style=\"font-size: 18px;\">【交通】</span></strong><span style=\"font-size: 18px;\">乘坐12路、16路公交车到北背岭站下车，小区就在站牌的对面。</span></p><p><strong><span style=\"font-size: 18px;\">【幼儿园】</span></strong><span style=\"font-size: 18px;\">银海区机关幼儿园、东方之星幼儿园</span></p><p><strong><span style=\"font-size: 18px;\">【中小学】</span></strong><span style=\"font-size: 18px;\">银海区一小、北背岭小学、侨港华侨小学、和兴小学、北海九中、华侨中学</span></p><p><strong><span style=\"font-size: 18px;\">【大学】</span></strong><span style=\"font-size: 18px;\">中央民族大学附中北海国际学校、北京航天航空大学北海学院、桂林电子科技大学北海学院、北海艺术设计职业学院、合浦卫样</span></p><p><strong><span style=\"font-size: 18px;\">【综合商场】</span></strong><span style=\"font-size: 18px;\">银滩旅游购物中心、银滩购物商场</span></p><p><strong><span style=\"font-size: 18px;\">【医院】</span></strong><span style=\"font-size: 18px;\">侨港华侨医院、第三人民医院</span></p><p><strong><span style=\"font-size: 18px;\">【其他】</span></strong><span style=\"font-size: 18px;\">有12路、16路公交车</span></p><p><strong><span style=\"font-size: 18px;\"><br/></span></strong></p><p><strong><span style=\"font-size: 18px;\">【小区内部配套】</span></strong><span style=\"font-size: 18px;\">室外恒温泳池、会所</span></p><p><span style=\"color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;; font-size: 14px; line-height: 33px; widows: 1; background-color: rgb(255, 255, 255);\"><br/></span></p>', '2018-03-14 00:53:00.801000', '1', '1', '昊海梧桐', '北海银海区银滩大道5号', '49.52', 