INSERT INTO `aj_premise` VALUES ('18', '<p><span style=\"font-size: 18px;\">2018-02-20&nbsp;17:09:35</span></p><p><span style=\"font-size: 18px;\">【项目简介】</span><span style=\"font-size: 18px;\"></span><span style=\"color: rgb(102, 102, 102); font-family: arial, helvetica, sans-serif; font-size: 18px; line-height: 30px; text-indent: 24px; background-color: rgb(255, 255, 255);\">华一红树林位于北海市四川南路，处于北海市旧城中心与新城中心之间，距旧城中心城区北部湾广场约4.5公里，距新城中心约4公里，距北海银滩约3公里。华一红树林是华一控股集团旗下的北海华一中联房地产开发有限公司在北海市倾力打造的颇具园林特色的住宅项目。为打造北海市一流的园林住宅小区，华一红树林项目特别聘请了全国一流的建筑设计单位和全国一流的景观设计单位，分别为项目建筑和景观进行了精心设计。华一红树林总投资6.3亿元，华一红树林项目预计分两期建设开发，拟建11栋建筑：其中5栋33层住宅楼，1栋34层住宅楼，3栋18层住宅楼，1栋8层住宅楼，1栋3层商务会所。</span></p><p><br/></p><p><span style=\"font-size: 18px;\">【学校】聪明泉幼儿园，银滩镇中心小学，名海国际幼儿园，北海市华侨中学，童之梦国学幼儿园，北海市外国语实验学校。</span><br/></p><p><span style=\"font-size: 18px;\"><br/></span></p><p><span style=\"font-size: 18px;\">【购物】茂丰加油站-便利让，何李养小卖部，小麦便利店，好福超市（正五店）</span></p><p><span style=\"font-size: 18px;\"><br/></span></p><p><span style=\"font-size: 18px;\">【生活】中国建设银行24小时自助银行（高新区支行），中国建设银行（北海高新区支行），北海市区农村信用合作联社侨港信用社。</span></p><p><span style=\"font-size: 18px;\"><br/></span></p><p><span style=\"font-size: 18px;\">【娱乐】保利国际影城，侨港文体公园，动感4D影院，侨港文化公园，万达影城（北海店），江尾公园。</span></p><p><span style=\"font-size: 18px;\"><br/></span></p><p><span style=\"font-size: 18px;\">【餐饮】北海渔村，绿香苑凉粉店，京城，竹林渔乡美食，重庆过江鱼庄，农家乐快餐店，十八妹快餐。</span></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p>', '2018-02-20 17:57:40.318000', '1', '1', '华一红树林', '北海市银海区华一红树林', '60', '0.00', '176899', '北海华一中联房地产开发有限公司', '12', '新房2018-034', '3.49', '35.2%', '30', '【共12栋：其中高层住宅10栋，分别是18层3栋，33层6栋，34层1栋，8层1栋，独立会所1栋】', '900', '/static/upload/image/20171215/1513329068633009025_wt.jpg', null, '华一物业', '1.5元/m²/月', '2365', '银海区四川南路与浙江路交汇处（1栋售楼部）', '3', '', '1', '2', '40', '21.44034', '109.133814', '四川路城市主干道，社区配套齐全，交通便利，市场，酒店，学校', '【该楼盘已售罄】', '2', null, '', '2', '2');
