INSERT INTO `aj_premise` VALUES ('27', '<p><span style=\"font-size: 18px;\">2018-02-06&nbsp;19:20:30</span><br/></p><p><strong><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">【项目简介】</span></span></strong><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51);\"></span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">“</span><strong><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">中电海湾国际社区</span></strong><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">”</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">总建筑面积约</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">100</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">万平方。地处广西北海市东区</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">“</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">海湾新城</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">”</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">是北海政府重点规划项目。</span></span></p><p><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51);\"><br/></span></span></p><p><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">【<strong>中电海湾国际社区】</strong></span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">位于北海城市副中心，规划</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">21</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">平方公里的北部湾智慧生态电子城核心区域，</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">172</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">亩全新商业配套，五星级酒店、</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">3000</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">平方双语幼儿园、教堂等应有尽有；此外，社区一期配套上海华联超市、泰式餐厅、</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">De Bella</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">咖啡、银行、通信营业厅等、网球场、蓝球场、乒乓球室、室内羽毛球场等，比肩美国加州硅谷湾区。</span></span></p><p><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51);\"><br/></span></span></p><p><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">【</span><strong><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">中电海湾国际社区】</span></strong><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">项目占地</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">450</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">亩</span> <span style=\"font-size: 18px; color: rgb(51, 51, 51);\">容积率</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">2.5 </span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">绿化面积</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">35.4%</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">，总建筑面积：</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">887494.18</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">㎡，总户数</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">9327</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">户。一期会所</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">101#</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">占地约</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">1800</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">平方，三期幼儿园</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">100#</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">占地约</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">3600</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">平方，多层共计</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">80</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">栋</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">(</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">含会所</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">)</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">高层共计</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">22</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">栋</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">(17</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">栋板式楼</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\"> 5</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">栋点式</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">)</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">现一期</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">18</span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">万平米的别墅、已经破土动工。</span> 　</span></p><p><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51);\"><br/></span></span></p><p><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">【<strong>中电海湾国际社区】</strong></span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">意大利托斯卡纳风情，低、多层住宅采用托斯卡纳地中海建筑风格，建筑秉持新古典主义建筑风格，整体以简约、现代的风格为主。高层外立面主体采用真石漆，局部石材铺砌，凸显尊贵感；低层住宅采用筒瓦、涂料局部石材，以充满时间印痕的建筑语绘和内敛、淡定之势、见证一个城市的变迁和发展、历久弥新。</span></span></p><p><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51);\"><br/></span></span></p><p><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">【<strong>中电海湾国际社区】</strong></span><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">定位为智能化高端社区，是居住、商业、养生、教育一体的国际级生活城。</span> <span style=\"font-size: 18px; color: rgb(51, 51, 51);\">“中电海湾国际社区”低、多层住宅充满浓郁的意大利托斯卡纳风情地中海新古典主义建筑风格，项目整体以简约、现代的风格为主，突出宜居性，引入海元素，以浅色调为主。</span> <span style=\"font-size: 18px; color: rgb(51, 51, 51);\">一梯两户，简化邻里关系，尊贵私密；</span> <span style=\"font-size: 18px; color: rgb(51, 51, 51);\">主卧朝南，大飘窗设计静享自然浪漫情怀；</span> <span style=\"font-size: 18px; color: rgb(51, 51, 51);\">全明客厅，奢华尺度面宽设计，随享观景采风阳台；</span> <span style=\"font-size: 18px; color: rgb(51, 51, 51);\">主客卧合理分区各享静谧，阔绰空间舒适体验。</span></span></p><p><strong style=\"font-size: 18px; background-color: white;\"><span style=\"color: rgb(51, 51, 51);\"><br/></span></strong></p><p><strong style=\"font-size: 18px; background-color: white;\"><span style=\"color: rgb(51, 51, 51);\">【楼层状况】</span></strong><span style=\"font-size: 18px; color: rgb(51, 51, 51); background-color: white;\">一期部分已建四栋</span><span style=\"font-size: 18px; font-family: Arial, sans-serif; color: rgb(51, 51, 51); background-color: white;\">9</span><span style=\"font-size: 18px; color: rgb(51, 51, 51); background-color: white;\">层洋房；二期包括北侧高层（户型以</span><span style=\"font-size: 18px; font-family: Arial, sans-serif; color: rgb(51, 51, 51); background-color: white;\">1</span><span style=\"font-size: 18px; color: rgb(51, 51, 51); background-color: white;\">居及</span><span style=\"font-size: 18px; font-family: Arial, sans-serif; color: rgb(51, 51, 51); background-color: white;\">2</span><span style=\"font-size: 18px; color: rgb(51, 51, 51); background-color: white;\">居为主），中心区高层（</span><span style=\"font-size: 18px; font-family: Arial, sans-serif; color: rgb(51, 51, 51); background-color: white;\">2</span><span style=\"font-size: 18px; color: rgb(51, 51, 51); background-color: white;\">居及小</span><span style=\"font-size: 18px; font-family: Arial, sans-serif; color: rgb(51, 51, 51); background-color: white;\">3</span><span style=\"font-size: 18px; color: rgb(51, 51, 51); background-color: white;\">居），核心区建筑</span><span style=\"font-size: 18px; font-family: Arial, sans-serif; color: rgb(51, 51, 51); background-color: white;\">120</span><span style=\"font-size: 18px; color: rgb(51, 51, 51); background-color: white;\">平以下舒适三居）及部分别墅；</span><span style=\"font-size: 18px; background-color: white;\"> </span><span style=\"font-size: 18px; color: rgb(51, 51, 51); background-color: white;\">三期北侧高层（户型以</span><span style=\"font-size: 18px; font-family: Arial, sans-serif; color: rgb(51, 51, 51); background-color: white;\">2</span><span style=\"font-size: 18px; color: rgb(51, 51, 51); background-color: white;\">居及</span><span style=\"font-size: 18px; font-family: Arial, sans-serif; color: rgb(51, 51, 51); background-color: white;\">1</span><span style=\"font-size: 18px; color: rgb(51, 51, 51); background-color: white;\">居为主），中心区高层（</span><span style=\"font-size: 18px; font-family: Arial, sans-serif; color: rgb(51, 51, 51); background-color: white;\">2</span><span style=\"font-size: 18px; color: rgb(51, 51, 51); background-color: white;\">居及小</span><span style=\"font-size: 18px; font-family: Arial, sans-serif; color: rgb(51, 51, 51); background-color: white;\">3</span><span style=\"font-size: 18px; color: rgb(51, 51, 51); background-color: white;\">居），核心区建筑</span><span style=\"font-size: 18px; font-family: Arial, sans-serif; color: rgb(51, 51, 51); background-color: white;\">120</span><span style=\"font-size: 18px; color: rgb(51, 51, 51); background-color: white;\">平以下舒适三居）及部分别墅；四期开发小高层、洋房及中央别墅。</span></p><p><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51);\"></span></span><br/></p>', '2018-03-14 01:02:58.973000', '1', '1', '中电海湾国际', '北海市海城区中电海湾国际社区', '450', '6500.00', '881101', '北京正宏置业集团', '114', '新房2018-020', '2.5', '30%', null, '【在售楼栋是2#、3#、4#楼】【主推户型是68-91平两房至四房，非毛坯房】【目前一期在建1#--4#楼全部已封顶，预计2018年11月底交房】【产权50年】', '3760', '/static/upload/image/20180314/1520960562910029799_wt.jpg|/static/upload/image/20171228/1514444382108019152_wt.jpg|/static/upload/image/20171204/1512347852483051407_wt_wt_wt_wt_wt_wt.jpg|/static/upload/image/20171204/1512347897639071475_wt_wt_wt_wt_wt_wt.jpg|/static/upload/image/20171204/1512347835811000227_wt_wt_wt_wt_wt_wt.jpg|/static/upload/image/20171204/1512347860483044669_wt_wt_wt_wt_wt_wt.jpg|/static/upload/image/20171204/1512347869967095175_wt_wt_wt_wt_wt_wt.jpg|/static/upload/image/20171204/1512347875545004488_wt_wt_wt_wt_wt_wt.jpg|/static/upload/image/20171204/1512347881014004612_wt_wt_wt_wt_wt_wt.jpg|/static/upload/image/20171204/1512347885030043758_wt_wt_wt_wt_wt_wt.jpg|/static/upload/image/20171204/1512347888951015872_wt_wt_wt_wt_wt_wt.jpg|/static/upload/image/20180206/1517904244212024987_wt.jpg|/static/upload/image/20180206/1517904215275080532_wt.jpg|/static/upload/image/20180206/1517904229212003881_wt.jpg|/static/upload/image/20180206/1517904235337093398_wt.jpg', null, '', '2.00元/㎡·月 ', '7832', '海城区南珠大道与台湾路交汇处', '2', '', '1', '1', '36', '21.544332', '109.184783', '廉洲湾金滩，海景商服配套，泳池，学区房，星级会所，大型商业', '【均价6500元/m²】\r\n【提前打负责人电话+前台登记报备】', '1', null, '', '1', '2');
INSERT INTO `aj_premise` VALUES ('28', '<p style=\"margin-bottom:0;margin-bottom:0;line-height: 25px;background:white\"><span style=\"font-size: 18px;\"><span style=\"color: rgb(51, 51, 51);\">2018-02-06&nbsp;17:56:40</span></span></p><p style=\"margin-bottom:0;margin-bottom:0;line-height: 25px;background:white\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 宋体; color: rgb(51, 51, 51);\">【项目简介】</span></strong></span></p><p style=\"margin-bottom:0;margin-bottom:0;line-height: 25px;background:white\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 14px; color: rgb(51, 51, 51);\"></span></strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【三千海在中国地产界，拥有六个性】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\"><br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【首先】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">三千海是中国同时拥有六重景观的三千亩海景大盘。大海、沙滩、高尔夫球场、游艇码头、内海、古树林，尽收眼底。<br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【第二】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">三千海是中国大规模移植来自世界各地名贵古树的豪宅社区。总共有上万棵树龄超过千年的珍稀树种，形成了一个古树林的园林景观。一棵菩提树就有3千多年的树龄，已经可以追溯到商朝的历史了。<br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【第三】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">三千海是中国拥有千亩海上山地高尔夫球场的高端社区。球场设计由国际著名的尼克劳斯二世担纲。球场占地1200亩，是标准18洞，已被评为全球优质、具挑战性的的球场之一。<br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【第四】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">三千海是中国用新鲜海水打造内海的亲海住区。内海面积20万平方米，水深3-6米，是一个真正的东方威尼斯。内海中放养几万尾石斑鱼，坐在自家窗台就可以享受钓鱼的乐趣。<br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【第五】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">三千海是中国全部采用干挂天然石材打造百年建筑的石砌城邦。在美国，纽约帝国大厦的建筑工艺与我们是一样的，全干挂石材饰面历经百年，依然展现出非凡的魅力。三千海180万平方米的建筑，高层外墙全部采用剪力墙结构，没有砖墙，外墙全部干挂3公分厚的石材。别墅采用22公分厚的古堡级石材砌筑。整个项目石材用料之大，直接抬高了石材市场的价格。<br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【第六】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">三千海是中国能从自家开游艇出海度假的豪宅社区，私家游艇直接入户。国际游艇会的码头有108个泊位。豪华的12座定制式游艇别墅坐落在半岛上，拥有整个内海港湾，每座别墅占地6亩以上，拥有私家温泉。用12生肖分别代表12座游艇别墅。<br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\"><br/></span></strong></span></span></p><p style=\"margin-bottom:0;margin-bottom:0;line-height: 25px;background:white\"><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【三千海超级豪宅社区，还拥有12大配套】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\"><br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【首先】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">1200亩海上山地高尔夫球场；<br/> </span></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【第二个】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">12亿打造超五星级豪华酒店，酒店顶层设置停机坪；</span><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\"><br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【第三个】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">2.3公里私家银滩，海神欢乐广场；<br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【第四个】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">108个泊位的国际游艇会码头；<br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【第五个】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">万株古树名木园林，桃花岛叠水仙境；<br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【第六个】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">五大奢享型名流会所，内海泳池；<br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【第七个】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">威尼斯风情内海商业街，中西餐厅；<br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【第八个】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">休闲假日购物广场shopping mall，大型3D影院；<br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【第九个】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">名校、双语幼儿园贵族教育配套；<br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【第十个】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">养生医疗配餐中心，社区综合性服务中心；<br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【第十一个】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">业主专属砂岩雕琢人行隧道；<br/> </span><strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">【第十二个】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51);\">市政七彩柏油马路，6公里火山岩休闲步道，红木候车亭、书报亭，香樟树巡道。</span></span></span></p>', '2018-03-14 01:20:56.910000', '1', '1', '天隆三千海', '北海市银海区天隆三千海', '3000', '14200.00', '1681700', '天隆地产', '3', '新房2018-003', '1.70', '60%', null, '【在售珊瑚园6#、7#、8#楼现房】【户型：1房75--78m²，2房94m²，小3房128m²，3房138m²，大3房155㎡】【产权70年】', '1352', '/static/upload/image/20180314/1520960705895032851_wt.jpg|/static/upload/image/20180314/1520961642504032134_wt.jpg|/static/upload/image/20180103/1514942261290041736_wt.jpg|/static/upload/image/20180103/1514942265993009835_wt.jpg|/static/upload/image/20180103/1514942271712008794_wt.jpg|/static/upload/image/20180103/1514942276571080312_wt.jpg|/static/upload/image/20180103/1514942283649019216_wt.jpg|/static/upload/image/20180103/1514942288383082657_wt.jpg|/static/upload/image/20180103/1514942294743069103_wt.jpg|/static/upload/image/20180103/1514942305633010494_wt.jpg|/static/upload/image/20180103/1514942316883030421_wt.jpg|/static/upload/image/20180314/1520961609145058480_wt.jpg', null, '戴德梁行', '3.00元/㎡·月', '1736', '天隆三千海高尔夫会所售楼部（老售楼部）', '6', '', '1', '2', '31', '21.445872', '109.086161', '海景，豪宅，高尔夫大盘，内海，私人游艇码头', '【均价14200元/㎡】\r\n【提前打负责人电话+前台登记报备】\r\n【签约至2018.2.28止】', '1', null, '', '1', '1');
INSERT INTO `aj_premise` VALUES ('29', '<p><span style=\"font-size: 18px;\">2018-02-06&nbsp;19:20:07</span><br/></p><p><strong><span style=\"font-size: 18px;\">【项目简介】</span></strong><span style=\"font-size: 18px;\">恒大御景半岛地处北海市银滩西区，北邻金海岸大道，南至海景大道，邻近银滩公园、冠头岭国家森林公园、海洋之窗；</span></p><p><strong><span style=\"font-size: 18px;\"><br/></span></strong></p><p><strong><span style=\"font-size: 18px;\">【恒大御景半岛】</span></strong><span style=\"font-size: 18px;\">配套包括风情商业街、大型社区超市、幼儿园等。拥有一栋集餐饮、休闲保健、棋牌、健身中心、地面中心泳池、会议中心、球类馆、儿童活动中心于一体的酒店；</span></p><p><strong><span style=\"font-size: 18px;\"><br/></span></strong></p><p><strong><span style=\"font-size: 18px;\">【恒大御景半岛】</span></strong><span style=\"font-size: 18px;\">的住宅包含建筑面积87--90m²的两房、118--140m²的三房、150--170余m²的四房，都采用方正户型设计；南北通透；宽阔观景阳台；功能布局合理。</span></p>', '2018-02-09 11:42:00.521000', '1', '1', '恒大御景半岛', '北海市银海区恒大御景半岛', '560', '9800.00', '1000000', '北海南国天堂房地产开发有限公司', '44', '新房2018-039', '3.0', '66%', null, '【主推20#、21#楼，在售户型79㎡、86㎡二房，109㎡小三房，120㎡-135㎡三房】【预计2019年8月份精装交房】 【13#、14#、15#楼仅剩113㎡小三房在售】【产权70年】', '5731', '/static/upload/image/20171210/1512898442258012642_wt.jpg|/static/upload/image/20171210/1512898453524067919_wt.jpg|/static/upload/image/20171210/1512898460055044040_wt.jpg|/static/upload/image/20171210/1512898470602072580_wt.jpg|/static/upload/image/20171210/1512898480212046739_wt.jpg|/static/upload/image/20171210/1512898484243009798_wt.jpg|/static/upload/image/20171210/1512898487930038739_wt.jpg|/static/upload/image/20171210/1512898493212023866_wt.jpg|/static/upload/image/20171210/1512898497321087504_wt.jpg|/static/upload/image/20171210/1512898516165093844_wt.jpg|/static/upload/image/20171210/1512898536915018379_wt.jpg', null, '金碧物业', '住宅2.5元/月/平米，商铺3.5元/月/平米（均含电梯费）', '8128', '银海区银滩西区金海岸大道88号', '2', '', '1', '2', '31', '21.44068', '109.103372', '学区房，银滩景区，高尔夫球场，超市，酒吧，风情一条街', '【均价9800元/m²】【报备手续繁杂，拖欠佣金，不建议推广】', '2', null, '', '2', '2');
INSERT INTO `aj_premise` VALUES ('30', '<p><span style=\"font-size: 18px;\">2018-02-06&nbsp;20:38:54</span><br/></p><p><strong><span style=\"font-size: 18px;\">【项目简介】</span></strong><span style=\"font-size: 18px;\">嘉和冠山海总占地面积1500亩，总建筑面积100万平方米，项目以2：3的配套土地比，打造了滨海度假社区，从欧洲城堡群到海门球会同，从私属内海到沙滩浴场，从黄金航道至五星级度假服务，嘉和冠山海将度假住宅、体验园林、立体商业、星级服务、智能安防完美融合，实现了滨海休闲居住的体验。项目毗邻国际标准的18洞球场、双面果岭----海门高尔夫球场，背靠冠头岭国家森林公园。</span></p><p><strong><span style=\"font-size: 18px;\"><br/></span></strong></p><p><strong><span style=\"font-size: 18px;\">【嘉和冠山海】</span></strong><span style=\"font-size: 18px;\">一期是紧邻17、18号洞果岭的爱丁堡组团，总建筑面积20万平方米，由10栋18--33层白墙红顶的欧式城堡群组成，主要户型为43--93m²的一房、两房。爱丁堡组团西邻冠头岭国家森林公园和海门球会会所，面朝水上果岭，背靠世纪公园，是项目私属内海和黄金航道的起始端。</span></p><p><strong><span style=\"font-size: 18px;\"><br/></span></strong></p><p><strong><span style=\"font-size: 18px;\">【嘉和冠山海】</span></strong><span style=\"font-size: 18px;\">地处银滩西区，西靠冠头岭国家森林公园，南面北部湾，地理位置优越，远离喧嚣城市，适宜休闲度假之用；此外，项目在北海中轴主干道北海大道之上。</span></p>', '2018-03-14 03:03:35.785000', '1', '1', '嘉和冠山海', '北海市银海区嘉和冠山海', '1500', '10000.00', '1000000', '北海嘉和房地产有限公司', '19', '新房2018-040', '1.0', '70%', null, '【在售6期罗腾堡1#楼】【户型为55m²一房、75m²两房、88m²两房和120m²三房】【预计2019年11月26日罗腾堡1#精装交房】', '4000', '/static/upload/image/20180314/1520967678941098240_wt.jpg|/static/upload/image/20171210/1512899319347041846_wt.jpg|/static/upload/image/20171210/1512899306925075412_wt.jpg|/static/upload/image/20171210/1512899324222021956_wt.jpg|/static/upload/image/20171210/1512899337910027634_wt.jpg|/static/upload/image/20171210/1512899342629073107_wt.jpg|/static/upload/image/20171210/1512899348113009533_wt.jpg|/static/upload/image/20171210/1512899352863070238_wt.jpg|/static/upload/image/20171210/1512899356895065728_wt.jpg|/static/upload/image/20171210/1512899361895053253_wt.jpg|/static/upload/image/20171210/1512899366770081354_wt.jpg|/static/upload/image/20171210/1512899373348039463_wt.jpg|/static/upload/image/20171210/1512899378520013926_wt.jpg|/static/upload/image/20171210/1512899392755098384_wt.jpg|