INSERT INTO `aj_premise` VALUES ('31', '<p><span style=\"font-size: 18px;\">2018-02-06&nbsp;22:19:17</span><br/></p><p><strong><span style=\"font-size: 18px;\">【项目简介】</span></strong><span style=\"font-size: 18px;\">森海豪庭四期位于北海市金海岸大道，森海豪庭总占地715亩，总建筑面积达45万平方米，建筑密度仅17%，绿化率达52%，是可供3000户成功人士们休闲居住的上层温泉社区。园区配套齐全，规划建设欧陆风情别墅、排屋、高层海景公寓、准五星级酒店和商业街，选用智能化多重安防系统，并正在建设温泉保健休闲中心。</span></p><p><strong><span style=\"font-size: 18px;\"><br/></span></strong></p><p><strong><span style=\"font-size: 18px;\">【周边设施】【交通】</span></strong><span style=\"font-size: 18px;\">乘坐3路、5路公交车以及银滩旅游专线车到金海岸大道路口下车，再沿金海岸大道往东走100米左右。<br/></span></p><p><strong><span style=\"font-size: 18px;\">【小区内部配套】</span></strong><span style=\"font-size: 18px;\">两大豪华游泳池、两个专业网球场、一个羽毛球场、一空五星级度假酒店、一家迷你型电影院、20000m²的全功能商业街区，超大型停车场。</span></p><p><strong><span style=\"font-size: 18px;\"><br/></span></strong></p><p><strong><span style=\"font-size: 18px;\">【楼层状况】</span></strong><span style=\"font-size: 18px;\">四期由1#--6#楼高层建筑组成。</span></p>', '2018-02-07 15:34:17.036000', '1', '1', '森海豪庭', '北海市银海区北海金昌森海豪庭四期', '715', '8800.00', '450000', '北海市金昌房地产开发有限公司', '6', '新房2018-041', '1.15 ', '52%', null, '【在售高层海景房 ，大两房87m²】【精装带品牌家具家电，拎包入住】【最后两套，最后两套，最后两套】', '400', '/static/upload/image/20171210/1512900117779093370_wt.jpg|/static/upload/image/20171210/1512900070029063971_wt.jpg|/static/upload/image/20171210/1512900077045096469_wt.jpg|/static/upload/image/20171210/1512900082060092031_wt.jpg|/static/upload/image/20171210/1512900099654081203_wt.jpg|/static/upload/image/20171210/1512900090560069532_wt.jpg|/static/upload/image/20171210/1512900095357084427_wt.jpg|/static/upload/image/20171210/1512900104092035050_wt.jpg|/static/upload/image/20171210/1512900108701018922_wt.jpg|/static/upload/image/20171210/1512900113920002273_wt.jpg', null, '绿城物业服务集团有限公司', '【高层物业2.5/m²/每月】【别墅3-3.5/m²/每月】', '1530', '银海区金海岸大道59号', '3', '', '1', '2', '28', '21.427114', '109.145829', '豪华泳池，网球场，羽毛球场，五星级度假酒店，电影院，全功能商业街', '【均价8800元/m²】\r\n【报备：提前打负责人电话报备】', '2', null, '', '2', '2'