INSERT INTO `aj_premise` VALUES ('44', '<p><span style=\"font-size: 18px;\">2018-02-07&nbsp;10:25:26</span></p><p><span style=\"font-size: 18px;\"><strong>【项目简介】</strong>禧悦湾位居银滩板块中心区位，占地约87亩，总建筑面积21万平米。南临浙江路，东近南京路。</span></p><p><span style=\"font-size: 18px;\"><strong><br/></strong></span></p><p><span style=\"font-size: 18px;\"><strong>【楼盘卖点】</strong>坐享2大公园，高绿化、低密度，纯板式结构、南北通透，敞开式入户，独门独户，楼间距宽50米至100米，通透性好，赠送面积大，北海唯一6米层高复式，一期现房销售。物业9S服务，被评为：2016年房地产景观楼盘，2016年畅销楼盘，北海房地产诚信经营企业。<strong><br/></strong></span></p><p><span style=\"font-size: 18px;\"><strong><br/></strong></span></p><p><span style=\"font-size: 18px;\"><strong>【周边</strong><strong>设施】</strong>小区内打造7000多平米海风情商业街，小区内建有500平米游泳池，侨港风情街，大润发旗舰店，万达广场，侨港中心医院，人民医院南院，机关幼儿园，银滩一小，华侨中学，北海实验外国语学校，银滩度假区，杭州路便民市场。</span></p><p><span style=\"font-size: 18px;\"><strong>【交通】</strong>公交3、5路到北海高新区站往北走到浙江路，再往东走约400米即到。</span></p><p><span style=\"font-size: 18px;\"><strong>【小区内部配套】</strong>亚热带海洋风情园林、游泳池、儿童游乐区、健身区、慢跑路径等。</span></p><p><span style=\"font-size: 18px;\"><strong><br/></strong></span></p><p><span style=\"font-size: 18px;\"><strong>【楼层状况】</strong>1、4、7栋33层，5、6栋32层，2、3栋31层，8、9栋25层</span></p>', '2018-03-14 02:07:59.223000', '1', '1', '亿兆禧悦湾', '北海市银海区禧悦湾', '87', '9500.00', '214562', '北海亿兆房地产开发有限公司', '9', '新房2018-013', '3.0', '40%', null, '【在售一期现房9#楼复式3房75m²+31m²、3房85m²+21m²】【预售二期3#、10#、8#、11#楼1房--4房，面积43m²--146m²】【2019年9月30日交房】【产权70年】', '1185', '/static/upload/image/20180314/1520964468941058675_wt.jpg|/static/upload/image/20180116/1516112948498080601_wt.jpg|/static/upload/image/20180116/1516112958888017136_wt.jpg|/static/upload/image/20180116/1516112983341030992_wt.jpg|/static/upload/image/20180116/1516117672685064817_wt.jpg|/static/upload/image/20180116/1516117692904079956_wt.jpg|/static/upload/image/20180116/1516117703029027130_wt.jpg|/static/upload/image/20180116/1516117714591082076_wt.jpg|/static/upload/image/20180116/1516117750982081005_wt.jpg|/static/upload/image/20180116/1516117761294015474_wt.jpg|/static/upload/image/2018