INSERT INTO `aj_premise` VALUES ('45', '<p><span style=\"font-size: 18px;\">2018-02-07&nbsp;17:25:13</span></p><p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong style=\"line-height: 33px;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51);\">【项目简介】</span></strong><strong style=\"line-height: 33px;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-weight: normal;\">桐洋新城位于北海市中部，广东路以东，西南大道南侧。用地方正，四面环路，北临西南大道，南面、东面、西面临规划路。桐洋新城北面为西南大道，南面有气象站和铁路，西面为已建小区和规划回建用地，东面为已建大型小区，规划小学及幼儿园。总投资85540万元，建设总用地面积163173平方，总建筑面积54380平方，其中住宅建筑面积585086平方，商业建筑面积16041平方，幼儿园建筑面积630平方，物业管理建筑面积1458平方，地下室面积176722平方。</span></strong></span></p><p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong style=\"line-height: 33px;\"><span style=\"font-size: 18px; font-family: 宋体; color: rgb(51, 51, 51);\"><br/></span></strong></span></p><p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong style=\"line-height: 33px;\"><span style=\"font-size: 18px; font-family: 宋体; color: rgb(51, 51, 51);\">【周边设施】【交通】</span></strong><strong style=\"line-height: 33px;\"><span style=\"font-size: 18px; font-family: 宋体; color: rgb(51, 51, 51); font-weight: normal;\">乘坐103路公交车到西南大道广东路口站下车，再沿着西南大道向东走300米，即可到达项目地。</span></strong></span></p><p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong style=\"line-height: 33px;\"><span style=\"font-size: 18px; font-family: 宋体; color: rgb(51, 51, 51); font-weight: normal;\"><br/></span></strong></span></p><p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong style=\"line-height: 33px;\"><span style=\"font-size: 18px; font-family: 宋体; color: rgb(51, 51, 51);\">【幼儿园】</span></strong><strong style=\"line-height: 33px;\"><span style=\"font-size: 18px; font-family: 宋体; color: rgb(51, 51, 51); font-weight: normal;\">金隆幼儿园、六一幼儿园、阳光幼儿园、上海路联合幼儿园、高德幼儿园。</span></strong></span></p><p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong style=\"line-height: 33px;\"><span style=\"font-size: 1