INSERT INTO `aj_premise` VALUES ('49', '<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">2018-02-07&nbsp;00:11:15</span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: arial, helvetica, sans-serif;\">【项目简介】</span></strong><span style=\"font-size: 16px; font-family: arial, helvetica, sans-serif;\"><br/></span></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: arial, helvetica, sans-serif;\">【东盟国际商贸城】</span></strong><span style=\"font-size: 16px; font-family: arial, helvetica, sans-serif;\">由北海湾春投资开发公司投资开发的北海东盟国际商贸城项目，其中一期项目总用地约773332.87平米，建设面积314782.37平米，项目整体业态包括大型商贸城、时尚步行街、精品公寓、高级酒店、家居建材广场等。后期计划建造仓储物流中心和电子商务平台，协助入驻商家全方位完善线上线下的交易网络。</span></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: arial, helvetica, sans-serif;\"><br/></span></strong></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: arial, helvetica, sans-serif;\">【本项目首期】</span></strong><span style=\"font-size: 16px; font-family: arial, helvetica, sans-serif;\">A地块规划为东盟国际商贸城，即大型第六代专业市场，以“东盟特色旅游商品集散中心”为定位，重金打造泛北部湾地区一站式旅游商品MALL，这里将集结1000多家全国知名品牌商家进驻，涵盖东盟特产、珍珠饰品、服饰箱包、日化用品、文具玩具、酒店用品等多种类别。</span></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: arial, helvetica, sans-serif;\"><br/></span></strong></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: arial, helvetica, sans-serif;\">【楼层状况】</span></strong><span style=\"font-size: 16px; font-family: arial, helvetica, sans-serif;\">东盟国际商贸城业态分布，1号楼星际酒店；2号楼、3号楼居家建材生活馆；4号楼、5号楼、7号楼、8号楼为精品公寓和写字楼；6号楼义乌模式专业市场；9号楼东盟十国风情街。1号楼、4号楼、7号楼、8号楼5幢高层建筑高度18-20层错落布置。</span></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: arial, helvetica, sans-serif;\"><br/></span></strong></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: arial, helvetica, sans-serif;\">【周边设施】</span></strong></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: arial, helvetica, sans-serif;\">【交通</span></strong><span style=\"font-size: 16px; font-family: arial, helvetica, sans-serif;\">】从北部湾广场出发沿四川路到西南大道，再往西南大道东路开车到湖北路，右边手就是项目。</span></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: arial, helvetica, sans-serif;\">【小区内部配套】</span></strong><span style=\"font-size: 16px; font-family: arial, helvetica, sans-serif;\">室外商业步行街、大型商业影院，东南亚特色风情港、五星级酒店、商贸城和超大型娱乐广场。</span></span></p>', '2018-03-14 02:17:53.457000', '1', '1', '东盟国际商贸城', '北海市海城区北海东盟国际商贸城', '116', '9000.00', '300000', '北海湾春投资开发有限公司', '9', '商铺2018-002', '3.0', '', null, '【在售6#商铺】【面积为14.7-50m²，一层均价28000元/㎡，二层均价18000元/㎡，四层均价12000元/㎡】【在售8#楼41--57㎡公寓均价7600元/㎡】', '2320', '/static/upload/image/20180314/1520965066363048685_wt.jpg|/static/upload/image/20180121/1516532257697015759_wt.jpg|/static/upload/image/20180121/1516532260478040181_wt.jpg|/static/upload/image/20180121/1516532262759079695_wt.jpg|/static/upload/image/20180121/1516532265212073391_wt.jpg|/static/upload/image/20180121/1516532267181082023_wt.jpg|/static/upload/image/20180121/1516532270478065633_wt.jpg|/static/upload/image/20180121/1516532273025009857_wt.jpg|/static/upload/image/20180121/1516532275259081590_wt.jpg|/static/upload/image/20180121/1516532277587019501_wt.jpg|/static/upload/image/20180121/1516532279540051654_wt.jpg', null, '北海伟业联行物业服务有限公司', '', '1510', '海城区西南大道与湖北路交汇处', '6', '', '1', '1', '28', '21.472874', '109.16662', '东盟特色商品，民族特色旅游商品，义乌综合小商品，东盟十国风情街', '【价格：1万-4万/㎡不等，面积：11㎡-370㎡不等】\r\n【1-3年一次性返还租金，4-5年业主托管】\r\n【报备：提前打负责人电话+前台登记报备】', '1', null, '', '1', '2');
INSERT INTO `aj_premise` VALUES ('50', '<p><span style=\"font-size: 18px;\">2018-02-07&nbsp;15:37:37</span></p><p><span style=\"font-size: 18px;\"><strong>【项目卖点】</strong>海湾新城中心地带，3字投资价值洼地，户型方正，南北通透，高性价比海景房；占地2千余亩月饼小镇旅游观光经济建设区，总投资十亿余元。</span></p><p><span style=\"font-size: 18px;\"><strong><br/></strong></span></p><p><span style=\"font-size: 18px;\"><strong>【开发理念】</strong>阳光.西海岸项目定位为“宜居海湾.畅享新城”，拥有海湾新城的投资发展价值、原生态景观价值（绿化园林）和天然海景价值（海边红树木）三重价值相结合并体现出来，这是其他楼盘不具备的物质。</span></p><p><span style=\"font-size: 18px;\"><strong><br/></strong></span></p><p><span style=\"font-size: 18px;\"><strong>【项目配套】</strong>小区自身配套商业区、生活超市、大型烟烂醉如泥农贸市场、小学、幼儿园一应俱全。烟楼卫生院就近医疗为生活做保障，15分钟漫步海边超高负氧离子红树木居住带，让生活更加清新无忧；临海滨海居住带约20平方公里将建设成为海湾新城的金融、商住、养生、旅游等配套中心。</span></p><p><span style=\"font-size: 18px;\"><strong><br/></strong></span></p><p><span style=\"font-size: 18px;\"><strong>【交通】</strong>高铁站就在项目以北3公里的位置，从南宁到北海耗时不超过1小时，充分打造了高效时代的1小时生活圈；北海福成机场距离项目不到30分钟车程，目前全国各主要大城市都有直飞航班到北海。北海市同时拥有机场、国际客运码头、高速公路，这是北部湾三市（北海、钦州、防城港）所独有的，交通便利是其他北海湾城市所不能比拟的。</span></p>', '2018-02-07 15:44:28.832000', '1', '1', '阳光西海岸', '北海市合浦县阳光西海岸销售中心', '120', '3800.00', '260000', '北海阳光宝诚房地产有限公司', '23', '新房2018-019', '2.85', '35%', null, '【在售12#、13#、15#、16#楼】【户型：两房--79㎡、84m²、86㎡、89㎡】', '1310', '/static/upload/image/20180123/1516679740914046479_wt.jpg|/static/upload/image/20180123/1516679756914044890_wt.jpg|/static/upload/image/20180123/1516679789664002055_wt.jpg|/static/upload/image/20180123/1516679798571005209_wt.jpg|/static/upload/image/20180123/1516679818164009841_wt.jpg|/static/upload/image/20180123/1516679828789047159_wt.jpg|/static/upload/image/20180123/1516679857977020815_wt.jpg|/static/upload/image/20180123/1516679869555087848_wt.jpg|/static/upload/image/20180123/1516679872821075280_wt.jpg|/static/upload/image/20180123/1516679878586042451_wt.jpg|/static/upload/image/20180123/1516679903071092872_wt.jpg|/static/upload/image/20180123/1516679934149002636_wt.jpg|/static/upload/image/20180123/1516679945867026240_wt.jpg|/static/upload/image/20180123/1516680691742077644_wt.jpg|/static/upload/image/20180123/1516680735711053747_wt.jpg|/static/upload/image/20180123/1516680747149059776_wt.jpg|/static/upload/image/20180123/1516680756586028860_wt.jpg|/static/upload/image/20180123/1516680764477069884_wt.jpg', null, '北海新明园物业服务有限公司', '0.80元/㎡·月 ', '2266', '北海市合浦创业大道9号（阳光.西海岸售楼部）', '3', '', '1', '1', '47', '21.572015', '109.180466', '原生态绿化园林，海边红树林，交通便利，医院，学校，市场', '【均价3800元/m²】\r\n【提前打负责人电话+前台登记报备】', '2', null, '', '2', '2');
INSERT INTO `aj_premise` VALUES ('51', '<p><span style=\"font-size: 18px;\">2018-03-12&nbsp;11:28:20</span></p><p><span style=\"font-size: 18px;\"><strong>【项目简介】</strong>善水湾选址海城区澳门路与吉林路交汇，坐拥北海大道交通资源，贯穿北海在，交通方便；800米亲海距离。在政府与良性投资环境下，城市中心轴线进一步推进，医疗、学区、商业及市政配套逐步完善，生活便捷从容，居所价值亦不断攀升。<br/></span></p><p><span style=\"font-size: 18px;\"><strong><br/></strong></span></p><p><span style=\"font-size: 18px;\"><strong>【善水湾】</strong>打造新城绿色生态社区，秉承生态绿色主题的园林设计理念，以现代化手法创造出别具风情的绿色的人居花园，丝毫不失其隽秀雍荣的气度和浓郁的文化内涵。同时其巧妙的功能设计，造型各异的阳台，更道尽了善水湾对适宜尺度的追求。自然之风、清新的空气、珍稀植物营造出一个鲜花与绿草相映，建筑与美景相融的梦幻空间。</span></p><p><span style=\"font-size: 18px;\"><strong><br/></strong></span></p><p><span style=\"font-size: 18px;\"><strong>【知名物业】</strong>管理团队24小时x365天全方位为您提供服务。可视对讲门禁系统、家庭安防系统、周界防越报警系统、保安巡更管理系统等全方位智能化居家系统，尝试维护您的高尚生活，将关怀真正融入生活的每一个细节。超五星级服务，微笑到家，专属温馨服务，为业主提供体贴入微的管家式服务，让您生活舒无忧。</span></p><p><span style=\"font-size: 18px;\"><strong><br/></strong></span></p><p><span style=\"font-size: 18px;\"><strong>【项目分2期开发</strong>】一期3#、4#楼共17层，一层为商业，两梯四户，以78-84㎡的两房两厅，88-95㎡的三房两厅为主；5#、6#楼共20层，5#楼一层为商业，6#楼一二楼为商业，两栋楼均为两梯五户，以49-58㎡的一房一厅和102㎡的三房两厅为主。户户全明采光、透风明亮，6层以上可以观海，部分户型有送入户花园等。</span></p>', '2018-03-12 11:29:29.223000', '1', '1', '善水湾', '北海市海城区善水湾', '45', '0.00', '113400', '北海汇龙投资开发有限公司', '6', '新房2018-020', '3.0', '31%', null, '【均价在4400元/㎡，在售楼栋1#，2#】【户型：45㎡-103㎡的一房----三房】', '1308', '/static/upload/image/20180124/1516779395129056082_wt.jpg|/static/upload/image/20180124/1516779405520042087_wt.jpg|/static/upload/image/20180124/1516779469082099608_wt.jpg|/static/upload/image/20180124/1516779477645085665_wt.jpg|/static/upload/image/20180124/1516779489442053593_wt.jpg|/static/upload/image/20180124/1516779502520085347_wt.jpg|/static/upload/image/20180124/1516779508567015803_wt.jpg|/static/upload/image/20180124/1516779511051057458_wt.jpg|/static/upload/image/20180124/1516779531363009119_wt.jpg|/static/upload/image/20180124/1516779535442018303_wt.jpg|/static/upload/image/20180124/1516779541520087722_wt.jpg|/static/upload/image/20180124/1516779545629046397_wt.jpg', null, '北海市勤精物业服务有限公司', '1.20元/㎡·月 ', '1098', '北海市海城区吉林路21号（吉林路与澳门路交汇处）', '3', '', '1', '1', '47', '21.535581', '109.188204', '学区房，便民市场，医疗，超市，银行，', '【该楼盘已售罄】', '2', null, '', '2', '2');
INSERT INTO `aj_premise` VALUES ('52', '<p><span style=\"font-size: 18px;\">2018-02-07&nbsp;15:48:04</span></p><p><span style=\"font-size: 18px;\"><strong>【项目简介】</strong><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif; line-height: 33px; background-color: rgb(255, 255, 255);\">盛荟天地是由红星美凯龙集团和泓盛集团倾力打造的。项目位于北海大道与南珠大道交汇处，是北海主城区、合浦县、铁山港区的三角中心，进出北海的要道。总投资18亿元，总占地150多亩，总建筑面积近38万平方米。其中商业建筑面积约20万㎡，酒店、公寓建筑面积近10万㎡。作为北海全业态、全客群综合体项目，盛荟天地拥有众多业态：包括红星美凯龙家居MALL、希尔顿酒店、沃尔玛超市、星美国际影城、甲级写字楼、世纪天街、北欧风情步行街、室外商业步行街、一站式购物中心、非毛坯SOHO公寓等，项目建成后将成为北海乃至北部湾地区集购物、休闲、娱乐、商务、文化、旅游于一体的国际级大型城市综合体。</span></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif; line-height: 33px; background-color: rgb(255, 255, 255);\"><br/></span></strong></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif; line-height: 33px; background-color: rgb(255, 255, 255);\">【楼层状况】</span></strong><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif; line-height: 33px; background-color: rgb(255, 255, 255);\">—1</span><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif; line-height: 33px; background-color: rgb(255, 255, 255);\">至5层为商铺，2栋28层非毛坯SOHO公寓，1栋26层的酒店(含甲级写字楼)。项目整体规划“西广场”、“中央广场”、“东广场”、“红星美凯龙广场”四大主题广场、以及1栋26层酒店和2栋28层SOHO非毛坯公寓。其中负一层、一层、二层为北海大Shopping Mall；负二层、负一层、三层规划为大型停车场；四、五层规划为北海第一个空中步行街---世纪天街。</span></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif; line-height: 33px; background-color: rgb(255, 255, 255);\"><br/></span></strong></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif; line-height: 33px; background-color: rgb(255, 255, 255);\">【周边设施】</span></strong></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif; line-height: 33px; background-color: rgb(255, 255, 255);\">【交通】</span></strong><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif; line-height: 33px; background-color: rgb(255, 255, 255);\">项目位于进出北海市的必经之路，交通路网发达，比邻北海市最大的汽车总站——南珠汽车站，有五条公交线(11路、13路、15路、19路、101路)，距南北高速10分钟车程，距火车站、以高铁站以及银滩风景区10分钟车程，距合浦县15分钟车程，距福成机场20分钟车程，扼守交通咽喉要道。</span></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif; line-height: 33px; background-color: rgb(255, 255, 255);\">从市中心可以坐</span></strong><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif; line-height: 33px; background-color: rgb(255, 255, 255);\">11路、13路、15路、19路、101路到南珠汽车总站下车，小区在南珠汽车总站的对角。</span></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif; line-height: 33px; background-color: rgb(255, 255, 255);\"><br/></span></strong></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif; line-height: 33px; background-color: rgb(255, 255, 255);\">【小区内部配套】</span></strong><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif; line-height: 33px; background-color: rgb(255, 255, 255);\">超市、影院、精品百货、家居Mall、电器、酒店、一站式购物中心、大型餐饮美食广场、娱乐休闲会所、停车场，主题广场。</span></span></p><p><br/></p><p><br/></p><p><br/></p><p><span style=\"font-family: arial, helvetica, sans-serif; line-height: 33px; font-size: 18px; background-color: rgb(255, 255, 255);\"></span></p><p><br/></p><p><span style=\"font-size: 18px; font-family: arial, helvetica, sans-serif; line-height: 33px; background-color: rgb(255, 255, 255);\"><br/><br/></span></p><p><span style=\"font-family: arial, helvetica, sans-serif; line-height: 33px; font-size: 18px; background-color: rgb(255, 255, 255);\"><br/></span></p>', '2018-03-14 02:21:49.707000', '1', '1', '盛荟天地', '北海市海城区盛荟天地广场', '150', '8000.00', '380000'