INSERT INTO `aj_premise` VALUES ('55', '<p><span style=\"font-size: 18px;\">2018-02-07&nbsp;15:55:03</span></p><p><span style=\"font-size: 18px;\"><strong>【项目简介】</strong></span><span style=\"font-size: 18px;\">候鸟湾地处茶亭路与上海路交界处，小区主入口设置在茶亭路段。项目总占地面积为33142平方米，总建筑面积为148324平方米。由7栋高层依势层级而建（1号楼32层，2号楼26层），打造16.73%超低建筑密度。户型面积涵盖66-139平方的2-3房，主力户型为88-90平方之间2房2厅户型。</span></p><p><span style=\"font-size: 18px;\"><br/></span></p><p><strong><span style=\"font-size: 18px;\">【候鸟湾】</span></strong><span style=\"font-size: 18px;\">让步出近20000平方的土地建设园林，园林所占土地的价值过亿元，并斥资3000万移植了一座巴厘岛风情园林，创造了3大功能景区和7大休闲区及50多处景观节点，还植栽了100多种东南亚名贵植物，同时，喷泉、泳池等近2500平方的水景。</span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 20px;\">此外，</span></strong>候鸟湾不惜增加30%的建筑成本，全桩基础结构打造北海北岸超大的地下车库，东西各设置一个出入口，不仅人车分流，更实现了人车分层，机动车和非机动车全部进入地下，营造一种安全、静谧、无污染的居住环境。</span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 20px;\"><br/></span></strong></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 20px;\">【周边设施】</span></strong></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 20px;\">【交通】</span></strong>乘坐10路、15路公交车到上海路口站下车，再沿上海路往北走500米左右，小区就在上海路与茶亭路交汇处西南角。</span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 20px;\"><br/></span></strong></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 20px;\">【小区内部配套】</span></strong>停车场，超市，巴厘岛风情园林。</span></p><p><br/></p>', '2018-03-14 02:24:03.020000', '1', '1', '候鸟湾', '北海市海城区候鸟湾', '50', '7200.00', '180659', '北海源房地产开发有限责任公司', '2', '新房2018-024', '4.48', '52%', null, '【在售3#、4#楼，层高31层，3梯8户】【户型：3房105㎡朝北，2房90㎡南北通透，2房88㎡朝南，2房82㎡朝南】【2018年4月交房】【产权39年】', '984', '/static/upload/image/20180314/1520965404301003642_wt.jpg|/static/upload/image/20180127/1517043380480034945_wt.jpg|/static/upload/image/20180127/1517043388370001593_wt.jpg|/static/upload/image/20180127/1517043390355096377_wt.jpg|/static/upload/image/20180127/1517043392636019031_wt.jpg|/static/upload/image/20180127/1517043394698001601_wt.jpg|/static/upload/image/20180127/1517043398058017346_wt.jpg|/static/upload/image/20180127/1517043400370035321_wt.jpg|/static/upload/image/20180127/1517043403964062694_wt.jpg|/static/upload/image/20180127/1517043406339022809_wt.jpg|/static/upload/image/20180127/1517043409027076064_wt.jpg|/static/upload/image/20180127/1517043413823079364_wt.jpg|/static/upload/image/20180127/1517043429495045164_wt.jpg|/static/upload/image/20180127/1517043439964076451_wt.jpg|/static/upload/image/20180127/1517043442714044576_wt.jpg|/static/upload/image/20180127/1517043445839044258_wt.jpg', null, '北海市金帝物业服务有限公司', '2.20元/㎡·月', '1548', '北海市海城区茶亭路18号（上海路与茶亭路交汇处）', '2', '', '1', '1', '31', '21.497459', '109.14818', '人车分流，人车分层管理，0商业，52%绿化，2500㎡水景，全封闭式物管', '【均价7200-8000元/㎡】\r\n【提前打负责人电话+前台登记报备】\r\n【签约至2018年12月31日】', '1', null, '', '1', '2');
INSERT INTO `aj_premise` VALUES ('56', '<p><span style=\"font-size: 20px;\">2018-01-28&nbsp;14:17:21</span></p><p><span style=\"font-size: 18px;\"><strong>【项目简介】</strong>书香水韵花园项目处于北海市银海区南珠广场<span style=\"text-decoration: underline;\">500米</span>的位置，位于南珠大道与浙江路交汇处。从我们项目书香水韵出发<span style=\"text-decoration: underline;\">15分钟</span>可到达高铁站，南珠汽车站，国际客运港，距离福成机场只有<span style=\"text-decoration: underline;\">20分钟</span>车程。</span></p><p><span style=\"font-size: 18px;\">南珠广场规划面积是北部湾广场的<span style=\"text-decoration: underline;\">10倍</span>，未来2年内它将成为北海新的市中心，这里将建设成为繁华的城市路段，拥有大型的体育场馆群、、群艺馆、博物馆、青少年宫，北海市政府和其他多个大型市政项目，是目前北海具有发展商业、行政、教育区域。项目周边<span style=\"text-decoration: underline;\">5分钟</span>的配套，向北<span style=\"text-decoration: underline;\">500米</span>可到南珠广场，向南<span style=\"text-decoration: underline;\">400米</span>可到北海园博园，我侧和西侧是我们规划建设的<span style=\"text-decoration: underline;\">3万亩</span>滨海湿地公园。湿地公园建成后，小桥流水的景观，也将是我们业主窗外靓丽的风景线。</span></p><p><span style=\"font-size: 18px;\">该项目属于学区房，北边一墙之隔就是桂林电子科技学院。附近还有公立驿马小学、北海国际学校（课程涵盖从学前班到高中）。项目对面就是北海市最大的图书馆，给我们业主子女带来不同的生活与学习环境。而且正在建设中的三甲级医院（北海市第二人民医院）已经封顶，预计年底就能投入使用，为我们业主的健康起到保驾护航的作用。</span><br/></p><p><span style=\"font-size: 18px;\">项目周边<span style=\"text-decoration: underline;\">8分钟</span>的生活圈，可达红树木风景区、银滩景区、疍家风情小镇、碧海丝路大剧院、美术馆、嘉年华大型主题公园。大型商场有万达广场、和安超市、太平洋商业广场，东盟自贸城等。北海市政府也规划迁到南珠广场附近。所以您今后生活在我们社区是非常惬意和便捷的</span><span style=\"font-size: 20px;\"><strong>。</strong></span></p><p><strong><span style=\"font-size: 20px;\"><br/></span></strong></p><p><strong><span style=\"font-size: 20px;\">【周边设施】</span></strong></p><p><strong><span style=\"font-size: 20px;\">【交通】</span></strong><span style=\"font-size: 20px;\">乘从16路、101路到桂电北海学院下车</span></p><p><strong><span style=\"font-size: 20px;\">【学校】</span></strong><span style=\"font-size: 20px;\">驿马小学、北海国际学校、桂林电子科技学院、北京航天大学、北海艺术学院</span></p><p><strong><span style=\"font-size: 20px;\">【综合商场】</span></strong><span style=\"font-size: 20px;\">南珠广场、万达广场、和安超市、太平洋商业广场、东盟自贸城</span><br/></p><p><strong><span style=\"font-size: 20px;\">【医院】</span></strong><span style=\"font-size: 20px;\">北海市第二人民医院</span></p><p><strong><span style=\"font-size: 20px;\">【其他】</span></strong><span style=\"font-size: 20px;\">体育场馆群、群艺馆、博物馆、青少年宫、红树林景区、银滩景区、疍家风情小镇、碧海丝路大剧院、美术馆、嘉年华大型主题公园</span></p><p><strong><span style=\"font-size: 20px;\">【小区内部配套】</span></strong><span style=\"font-size: 20px;\">小学、超市、商业街、运动场地、健身设施、儿童游乐场、游泳池</span></p><p><br/></p><p><br/></p>', '2018-03-14 02:25:36.176000', '1', '1', '书香水韵', '北海市银海区书香水韵', '100', '6500.00', '266538', '北海泉宇实业有限公司', '18', '新房2018-025', '3.5', '38%', null, '【在售1#---6#楼1房--5房（41--120㎡)】【主推6#1房51㎡（必须一次性付款），2房78㎡】【1#楼层高10层，2#楼层高17层，3#楼层高28层，于2018年5月毛坯交房。4#、5#、6#楼层高31层，于2018年年底毛坯交房】【产权66年】', null, '/static/upload/image/20180314/1520965528270048680_wt.jpg|/static/upload/image/20180205/1517826794447079843_wt.jpg|/static/upload/image/20180205/1517826796837002991_wt.jpg|/static/upload/image/20180205/1517826798853057100_wt.jpg|/static/upload/image/20180205/1517826802681064918_wt.jpg|/static/upload/image/20180205/1517826805228030383_wt.jpg|/static/upload/image/20180205/1517826807978096721_wt.jpg', null, '', '2元/㎡·月', '3500', '北海市南珠大道与浙江路交汇处（桂林电子科技学院旁）', '2', '', '1', '2', '36', '21.453143', '109.180521', '学区房，超市，商业街，运动场，儿童游乐场，游泳池', '【均价6500--6800元/㎡】\r\n【提前打负责人电话+前台登记报备】\r\n【签约至2019年2月26日】', '1', null, '', '1', '2');
