INSERT INTO `aj_premise` VALUES ('61', '<p><span style=\"font-size: 18px;\">2018-02-07&nbsp;23:54:10</span><br/></p><p><strong><span style=\"font-size: 18px;\">【项目简介】</span></strong><span style=\"font-size: 18px;\">智弘银城绿洲地处广东路229号，占地面积4723平米，建筑面积58882平米，容积率为3.5，绿化率34.42%，小区周边交通方便，配套齐全。</span></p><p><strong><span style=\"font-size: 18px;\"><br/></span></strong></p><p><strong><span style=\"font-size: 18px;\">【项目卖点】</span></strong></p><p><span style=\"font-size: 18px;\">●广东路主干道，贯穿北海南北走廊，22路、17路、106路公交车，出行方便；</span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●生活便捷，便民市场，中小型超市，星级酒店，药店，银行等一应俱全；</span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●银滩度假区核心板块，东邻大江埠旅游区，南向银滩景区，西达侨港风情街；</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●新城核心商圈，与万达广场为邻，180亩城市广场，升值空间无限；</span></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●125亩生态休闲社区，聘请赛美国际建筑设计公司大手笔规划，居住舒适度高；</span></span></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●自身强大配套，国际幼儿园、五星级酒店、药店、银行等一应俱全；</span></span></span></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●二期楼王，大户之家，低公摊，高赠送面积，两房到五房可选；</span></span></span></span></span></span></span></p><p><span style=\"font-size: 18px;\">●星际物业，智能化管理，全天候红外线监控，管家式贴心服务；</span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\">●园林风格：泰式风情园林，一步一景，水系环绕，2010年荣获“最佳景观设计方案金奖”。</span></span></p><p><br/></p>', '2018-03-10 12:01:34.739000', '1', '1', '智弘银城绿洲', '北海市银海区银城绿洲-西门', '125', '8700.00', '368000', '北海智弘投资有限公司', '12', '新房2018-029', '3.5', '34%', null, '【在售4#楼，层高31层】【户型：2房86m²，3+1房132m²，4+1房170m²】【毛坯交房】【产权70年】', '890', '/static/upload/image/20180220/1519097636662040754_wt.jpg|/static/upload/image/20180202/1517569387775077529_wt.jpg|/static/upload/image/20180202/1517569083057023734_wt.jpg|/static/upload/image/20180202/1517569095135063914_wt.jpg|/static/upload/image/20180202/1517569097588011896_wt.jpg|/static/upload/image/20180202/1517569106416069250_wt.jpg|/static/upload/image/20180202/1517569111744065351_wt.jpg|/static/upload/image/20180202/1517569121807088418_wt.jpg|/static/upload/image/20180202/1517569912557045326_wt.jpg|/static/upload/image/20180202/1517569929010040765_wt.jpg|/static/upload/image/20180202/1517569954963006710_wt.jpg|/static/upload/image/20180202/1517569960338052510_wt.jpg|/static/upload/image/20180202/1517569967228097069_wt.jpg', null, '北海智健物业服务有限公司', '2.10元/㎡·月 （含电梯费）', '1868', '银海区广东南路229号（万达广场南面）', '6', '', '1', '2', '28', '21.435382', '109.151084', '海景房，万达商圈，交通便利，近银滩，江尾公园，银滩公园', '【均价：8700元/m²】\r\n【提前打负责人电话+前台登记报备】\r\n【佣金结算：开发商付款后，当天结算】', '1', null, '', '2', '2');
INSERT INTO `aj_premise` VALUES ('62', '<p><span style=\"font-size: 18px;\">2018-03-10&nbsp;12:06:52</span><br/></p><p><span style=\"font-size: 18px;\">【项目简介】<span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">北海假日花园项目用地18252.37㎡，建筑面积216608.31㎡，现期推售的R栋为 33层，每层四个单元，3梯8户，总户数1056户，户型有一房、二房、三房。有的户型带入户花园，基本采用飘窗设计，增强通风采光度。实行人车分流，地下停车场2层共1200个车位。</span><br style=\"outline: none; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;; font-size: 14px; line-height: 33px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">北海·假日花园位于北海大道与云南路交汇处向西100米，项目门口横向的是北海大道，东上南珠汽车站直走高速，西至北海5A级国家森林公园冠头岭；东侧纵向的是云南路，北起百年老街、外沙海，南贯银滩、侨港。周边配套多所中小学、超市、市场、医院以及星级酒店和银行，统筹衣食住行娱教医疗一应俱全，经济轻松打造5生活圈。</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\"><br/></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">【周边设施】</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">【交通】可乘坐9路、11路、102路公交车到重庆路口云南路/北海大道云南路口/云南路市场站下车。</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">【小区内部配套】成熟园林中心，高级娱乐会所，老年活动中心，儿童娱乐场。</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\"><br/></span></span></span></p>', '2018-03-14 02:46:09.145000', '1', '1', '北海假日花园', '北海市海城区北海假日花园', '27', '5700.00', '87590', '北海汇澳投资发展有限公司', '1', '新房2018-030', '5.32', '30.2%', null, '', '1200', '/static/upload/image/20180314/1520966763395036544_wt.jpg|/static/upload/image/20180205/1517830546494051129_wt.jpg|/static/upload/image/20180205/1517830548728064073_wt.jpg|/static/upload/image/20180205/1517830550619051832_wt.jpg', null, '', '1.0元/m²/月', '1056', '海城区北海大道与云南路交汇处往西100米', null, '', '1', '1', '28', '21.467831', '109.105599', '', '', '1', null, '', '1', '2');
INSERT INTO `aj_premise` VALUES ('63', '<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">2018-02-08&nbsp;00:01:37</span><br/></p><p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">【<strong>项目简介</strong>】</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">东峰世纪公寓该项目用地位于北海市广东路以东、新世纪大道以南，总投资8亿人民币，规划总规划面积395613平米。住宅楼共18栋，其中层高30层的住宅楼4楼；层高27层的住宅楼4栋；层高28层的住宅楼2栋；层高18层的住宅楼5栋；层高6层的住宅楼3栋；沿街塔楼底两层为商业用楼。入住总户数2964户，入住总人数按每户3.5人计，为10374。</span></p><p><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><br/></span></strong></p><p><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">【周边配套】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><br/></span></p><p><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">【学校】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">驿马小学，银海区一小，北海第九中学，中央民族大学附中，北海国际学校，北海艺术设计职业学校，和兴小学，银海区六小；</span></p><p><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">【银行】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">中行，工行，建行，农行；</span></p><p><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">【公交路线】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">8路到本小区门口，12路到各兴居委会路口；</span></p><p><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">【购物广场】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">南宁百货，新大润发，年年丰农贸市场（离小区约200米）万达广场；</span></p><p><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">【旅游景点】</span></strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\">大江埠风景区，海洋之窗，冯家江旅游区。</span></p><p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><br/></span></p>', '2018-03-14 02:50:07.645000', '1', '1', '东峰世纪公寓', '北海市银海区东峰世纪公寓', '130', '5900.00', '295613', '北海东峰房地产开发有限公司', '18', '新房2018-031', '3.5', '30%', null, '【在售2房84m²，3房89m²、109m²，4房128m²】【毛坯现房】【产权70年】', '2993', '/static/upload/image/20180314/1520967001738028698_wt.jpg', null, '北海市苑翔物业服务有限责任公司', '1.2元/m²·月', '2946', '银海区新世纪大道221号（东峰世纪公寓售楼部）', '6', '', '1', '2', '28', '21.453491', '109.15003', '学区房，银行，医院，公交，购物广场，旅游景点', '【住宅均价5900元/m²，商铺均价22000元/m²】\r\n【提前打负责人电话+前台登记报备】\r\n', '2', null, '', '1', '2');
