INSERT INTO `aj_premise` VALUES ('65', '<p><span style=\"font-size: 18px;\">2018-02-07&nbsp;21:46:31</span></p><p><strong><span style=\"font-size: 18px;\">【项目简介】</span></strong><span style=\"font-size: 18px;\">锦江白鹭郡，地处湿地公园，南临园博园，北临冯家江，西靠南珠大道，距南珠广场2公里，距银滩3公里，是北海城市的生态中心，未来的商业文化中心。项目占地12.8万m²，在这珍贵的12.8万m²之上，大间距独立公布了32幢9--20层的板式现代建筑，其园林为法式庭园风格。由于是大间距独立分布，故每幢建筑四周有景，房房朝南，房房南北通透。在配套上，有1公里临江小街，1200m²购物广场，1600m²的高端幼儿园，8000m²公寓式酒店，1600m²养生会所，10000m²购物商厦。</span></p><p><span style=\"font-size: 18px;\"><br/></span></p><p><strong><span style=\"font-size: 18px;\">【周边设施】</span></strong></p><p><strong><span style=\"font-size: 18px;\">【交通】</span></strong><span style=\"font-size: 18px;\">公交16路、101路、18路、新世纪大道专线；</span></p><p><strong><span style=\"font-size: 18px;\">【中小学】</span></strong><span style=\"font-size: 18px;\">北海中学、北海国际学校；</span></p><p><strong><span style=\"font-size: 18px;\">【大学】</span></strong><span style=\"font-size: 18px;\">桂林电子科技大学北海分校；</span></p><p><strong><span style=\"font-size: 18px;\">【综合商场】</span></strong><span style=\"font-size: 18px;\">万达超市、和安超市、万达购物广场、东盟国际商贸城；</span></p><p><strong><span style=\"font-size: 18px;\">【医院】</span></strong><span style=\"font-size: 18px;\">北海市第二人民医院；</span></p><p><strong><span style=\"font-size: 18px;\">【其他】</span></strong><span style=\"font-size: 18px;\">湿地公园、园博园；</span></p><p><strong><span style=\"font-size: 18px;\">【小区内部配套】</span></strong><span style=\"font-size: 18px;\">幼儿园、商业街、运动场地、儿童游乐场、游泳池。</span></p>', '2018-03-14 02:54:19.848000', '1', '1', '锦江白鹭郡', '北海锦江白鹭郡', '193', '7300.00', '250000', '北海中鸿房地产开发有限公司', '31', '新房2018-035', '1.94', '50%', null, '【在售S8#、S9#楼，均价7300--7800元/m²；S8#毛坯交房，S9#楼精装+1000元/m²】【认筹1#、2#、3#楼，均价7300元/m²毛坯交房，精装+1000元/m²】【1#、2#、3#、S8#、S9#楼2019年9月交房】', '1708', '/static/upload/image/20180314/1520967248926083456_wt.jpg|/static/upload/image/20180205/1517826096619000923_wt.jpg|/static/upload/image/20180205/1517826120228066367_wt.jpg|/static/upload/image/20180205/1517826124462013853_wt.jpg|/static/upload/image/20180205/