INSERT INTO `aj_premise` VALUES ('66', '<p><span style=\"font-size: 18px;\">2018-02-08&nbsp;19:30:38</span><br/></p><p><strong><span style=\"font-size: 18px;\">【项目简介】</span></strong><span style=\"font-size: 18px;\">东峰国际公寓项目位于北海大道市公安局旁。项目是由北海东峰房地产开发有限公司开发，是北海较早的一批由房地产转型多元化经营的现代化企业。公司开发了包括悦发系列、东峰系列、桐洋系列等大大小小十余个项目。小区总占地面积60亩，绿化率30%，住宅总建筑面积为13万平方米，一共由7栋楼组成，总共1200多户。小区设计100%人车分流，充分考虑到业主的出行安全和道路畅通。小区共1200多个停车位，分为地下停车场和智能化停车楼。</span></p><p><span style=\"font-size: 18px;\"><br/></span></p><p><span style=\"font-size: 18px;\">【周边设施】</span></p><p><span style=\"font-size: 18px;\">【交通】乘坐11路、13路、18路、19路、21路、103路公交车，到公安局站下车，小区就在公安局旁边。</span></p><p><span style=\"font-size: 18px;\">【中小学】十七小学，北海第六中学。</span></p><p><span style=\"font-size: 18px;\">【市场】金葵菜市场、德正农贸市场。</span></p><p><span style=\"font-size: 18px;\">【小区内部配套】超市。</span></p>', '2018-02-09 23:39:48.865000', '1', '1', '东峰国际公寓', '北海市东峰国际', '60', '7000.00', '189000', '北海东峰房地产开发有限公司', '7', '新房2018-042', '3.5', '30%', null, '【在售6#楼层高17层、8#楼层高18层，2梯4户】【户型：两房86m²朝南，三房92m²、94m²朝南，四房111m²、129m²南北通透】【毛坯现房】【产权70年】', '1242', '/static/upload/image/20180208/1518086863552095686_wt.jpg|/static/upload/image/20180208/1518086866083063803_wt.jpg|/static/upload/image/20180208/1518086868458065049_wt.jpg|/static/upload/image/20180208/1518086870521059153_wt.jpg', null, '', '1.3元/m²/月', '1200', '海城区北海大道市公安局旁', '6', '', '1', '1', '36', '21.486295', '109.167837', '交通便利，地段好，质优，价廉', '【均价：5#楼7000元/m²；6#、8#楼6000元/m²】\r\n【报备：提前打负责人电话】', '1', null, '', '1', '1');
INSERT INTO `aj_premise` VALUES ('67', '<p><span style=\"font-size: 18px;\">2018-02-10&nbsp;19:27:01</span><br/></p><p><strong><span style=\"font-size: 18px;\">【项目简介】</span></strong></p><p><span style=\"font-size: 18px;\">1、项目身处北海大道中央商务圈，享受着成熟的市政配套。5分钟车程生活圈内，商场、菜市、学校、医疗、银行、车站、公园等一应俱全，全面呵护着业主的品质生活。南接新行政中心、北海大学及银滩风景区。</span></p><p><span style=\"font-size: 18px;\">2、户型丰富可选择性强，低密度、低容积率、高绿化、超宽楼间距，视野开阔。</span></p><p><span style=\"font-size: 18px;\">3、户型设计佳，户户南北通透，南北双阳台设计提高居住舒适感，开间尺寸达到最佳通风采光效果。</span></p><p><span style=\"font-size: 18px;\">4、一、二期已交付，入住率高，居住氛围浓厚，宜居宜住。</span></p><p><span style=\"font-size: 18px;\"><br/></span></p><p><strong><span style=\"font-size: 18px;\">【周边配套】</span></strong></p><p><strong><span style=\"font-size: 18px;\">【交通】</span></strong><span style=\"font-size: 18px;\">门中有103路公交车直达侨港浴场，北海大道专线、15路、13路公交等；</span></p><p><strong><span style=\"font-size: 18px;\">【学校】</span></strong><span style=\"font-size: 18px;\">第二实验小学、第17小、第6小、国际学校、上海路小学、九中，八中；</span></p><p><strong><span style=\"font-size: 18px;\">【商业】</span></strong><span style=\"font-size: 18px;\">和安宁春城、大润发商圈；</span></p><p><strong><span style=\"font-size: 18px;\">【菜市】</span></strong><span style=\"font-size: 18px;\">金葵市场、上海路便民市场、北海大道德正市场。</span></p><p><span style=\"font-size: 18px;\"><br/></span></p><p><span style=\"font-size: 18px;\">【户型特点】南北双阳台设计，南北通透板楼设计；户型方正利用率高，全明设计开间尺寸能达到最佳通风采光效果。</span></p>', '2018-02-11 23:22:33.380000', '1', '1', '东方明都', '北海市海城区东方明都销售中心', '90', '6500.00', '177052', '北海泰基房地产开发有限公司', null, '新房2018-043', '2.93', '30%', null, '【在售8#、10#楼，层高均27层，2梯4户】【户型：8#楼的两房77m²朝南，三房107m²南北通；10#楼的两房76m²南北通，三房106m²南北通，四房122m²南北通】【2019年年底毛坯交房】【产权26年】【】', '812', '/static/upload/image/20180210/1518261722646076566_wt.jpg|/static/upload/image/20180210/1518261728833070647_wt.jpg|/static/upload/image/20180210/1518261740052014635_wt.jpg|/static/upload/image/20180210/1518261754505072919_wt.jpg|/static/upload/image/20180210/1518261750302050007_wt.jpg|/static/upload/image/20180210/1518261752537071149_wt.jp