INSERT INTO `aj_premise` VALUES ('69', '<p><span style=\"font-size: 18px;\">2018-02-14&nbsp;15:42:00</span><br/></p><p><strong><span style=\"font-size: 18px;\">【项目简介】</span></strong></p><p><span style=\"font-size: 18px;\">●四通八达国际广场是防城港三生房地产有限公司联合北京四通集团强力打造的现代大型项目，总用地面积为391218m²，总建筑面积约为1402067m²。项目位于金花茶大道东面，近邻防城港高铁站北站和规划中的长途客运中心、公交总站，周边路网发达，交通极为便利，是集住宅、商务写字楼、高档酒店以及商业等功能于一体的大型复合商业金融地标性建筑群。</span></p><p><span style=\"font-size: 18px;\"><br/></span></p><p><span style=\"font-size: 18px;\">●离CBD（中央商务区）只有900米。1295亩的中央商务区将会有大量的国内外白领阶层在这里工作，带来大量的高素质阶层和租房需求，租房质量，租金价格必将一路高涨。</span></p><p><span style=\"font-size: 18px;\"><br/></span></p><p><span style=\"font-size: 18px;\">●由于紧挨高铁，作为这座城市的窗口，项目的建筑质量要求比一般楼盘高，项目地址原是一片风化石形成的小土坡，这样的地基牢固，没有一些乱七八糟的东西，采用框架结构，选用的是Ⅲ级及以上强度的钢筋，水泥强度为524MPa，建筑质量高。</span></p><p><span style=\"font-size: 18px;\"><br/></span></p><p><span style=\"font-size: 18px;\">●与中央商务区距离只有900米，同属一个商圈，未来中央商务区写字楼价格或租金高起的同时，四能八达的写字楼价格或租金不可能会毫无作为，现在中央商务区写字楼已基本售罄，可选性很少，四通八达国际广场将是您投资写字楼最值得考虑的楼盘。</span></p><p><br/></p><p><strong><span style=\"font-size: 18px;\">【楼层状况】</span></strong><span style=\"font-size: 18px;\">住宅18栋，28层，层差20元/m²左右；写字楼6栋，27层，层差30元/m²左右。</span></p>', '2018-03-15 15:00:24.126000', '1', '1', '防城港四通八达', '防城港市港口区四通八达国际广场', '588', '7500.00', '1402067', '防城港通达房地产开发有限公司', null, '新房2018-045', '2.89', '30%', null, '【在售二期D号地块泰城幸福里写字楼，2梯5户，层高28层】【户型：一房59m²，两房78m²、88m²，三房120m²】【在售商铺8--20m²起，均价14000元/m²，独立产权，返租三年】【产权70年】', null, '/static/upload/image/20180315/1521097216767050186_wt.jpg|/static/upload/image/20180214/1518594141822003860_wt.jpg|/static/upload/image/20180214/1518594144306081103_wt.jpg|/static/upload/image/20180214/1518594146728038951_wt.jpg', null, '', '', null, '防城港金花茶大道东面站前路两侧', '2', '', '1', '1', '36', '21.720893', '108.374803', '高铁，公交总站，海景，公寓，写字楼，商场，建材大市场，酒店娱乐', '【均价：住宅7500元/m²，商铺14000元/m²】\r\n【报备：提前打负责人电话】', '1', null, '', '1', '2');
