INSERT INTO `aj_premise` VALUES ('80', '<p><span style=\"font-size: 18px;\">2018-03-09&nbsp;10:03:06</span></p><p><span style=\"font-size: 18px;\">【项目简介】银滩圣美阳光家园位于广东路到银滩路地段，南面距银滩风景区约1800米，北面以公务员小区为邻，未来将建成15万m²的大型低密度亲海休闲社区，项目由花园洋房、多层住宅、小高层公寓、观海酒店等围合而成；二期工程由一栋11层、四栋12层和一栋17层的高层建筑组成；小区的园林景观由英国天夏国际景观设计公司打造，具有亚热带风情与文化内涵，使住户可以近观园内叠水银沙，充分体现人性化空间设计的理念。圣美阳光家园在楼宇规划、建筑施工、质量验收、产品素质、物业服务等方面都采取大手笔制作。</span><br/></p>', '2018-03-10 12:14:24.582000', '1', '1', '圣美阳光家园', '北海市银海区圣美阳光家园-东门', '0', '0.00', '0', '北海圣美房地产开发有限公司', '0', '二手房-016', '1.97', '30.8%', null, '', '0', '', null, '北海银湾海天物业在服务有限公司', '0.8元/m²/月', '0', '银海区广东路188号（广东路西侧，江苏路南侧）', '3', '', '1', '2', '28', '21.433009', '109.150775', '', '', '2', null, '', '2', '2');
