INSERT INTO `aj_premise` VALUES ('82', '<p><span style=\"font-size: 18px;\">【项目简介】</span></p><p><span style=\"font-size: 18px;\">1、项目位于北海市主干道北京路，交通枢纽和核心之所在，出行便利，四通八达。</span></p><p><span style=\"font-size: 18px;\">2、绝佳中心地段，毗邻宁春城、大润发超市、高铁站，升值潜力巨大，适宜投资与自住。</span></p><p><span style=\"font-size: 18px;\">3、成熟配套，5分钟生活圈大型超市、大型商场、市场、医院、学校、银行等一应俱全。</span></p><p><span style=\"font-size: 18px;\">4、拥有国际五星级酒店标准的资质企业“和乐酒店管理公司”为业主提供贴心、专业的星级物业管理服务。</span></p><p><span style=\"font-size: 18px;\">5、酒店返租，年回报率高达8%，投资首选！</span></p><p><span style=\"font-size: 18px;\">6、户型舒适宜居，坐北朝南，具有极好的景观及日照。</span></p><p><span style=\"font-size: 18px;\"><br/></span></p><p><span style=\"font-size: 18px;\">【建筑风格】现代建筑风格，法式风情，结合北海当地特殊的人文地理环境的度假社区。</span></p><p><span style=\"font-size: 18px;\"><br/></span></p><p><span style=\"font-size: 18px;\">【楼层状况】由一栋住宅公寓楼，一栋酒店组成。公寓楼为26层坐北朝南，28层的酒店（1--2层为商业，3--4层为住宅，5层是公共配套，6--19层为酒店。20--28层为写字楼）</span><br/></p>', '2018-03-14 00:13:26.129000', '1', '1', '和居会', '北海市海城区和居会', '6.8', '0.00', '39607', '北海市和易地产有限公司', '2', '新房2018-049', '7', '28%', null, '【2018年4月份开盘】【认筹户型：1+1房和两房，63--77m²】【三梯11户、两梯8户。公寓26层，酒店28层】【2018年6月精装/毛坯交房】', '159', '/static/upload/image/20180314/1520957589441059926_wt.jpg', null, '北海和乐酒店管理公司', '2.5元/m²/月', '432', '北海市北京路与重庆路交汇处（宁春城旁）', '4', '', '1', '1', '36', '21.467694', '109.131113', '', '【均价未定；现处于认筹阶段：2万抵5万】\r\n【报备：提前打负责人电话+微信群报备】', '1', null, '', '1', '2');
