INSERT INTO `aj_premise` VALUES ('92', '<p><span style=\"font-size: 18px;\">【项目简介】<span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">中兴金海湾位于广西北海市广东南路与浙江路交汇处以东250米处，南靠廉政公园，西临北海市公务员小区，东北比邻广西独特的4A级纯生态型大江埠自然风景区，南距具有“天下滩”美誉之称的旅游度假区北海银滩约2公里，离市中心不到5公里。区域位置优越性突显，交通便利，既可享受城市的繁华，又过滤尘世的干扰；潜力巨大，是北海市民及国内外各方人士理想的投资、居住、度假、休养之选择。</span><br style=\"outline: none; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;; font-size: 14px; line-height: 33px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\"><br/></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">中兴金海湾总用地面积为56133㎡，建筑密度27.48%，容积率1.64，绿化率35.50%。小区内环境幽雅宁静、富有情调；中心庭院精心构筑，珍稀植栽赏心悦目，漫步小径，观赏摇曳多姿的红花绿叶自然生态，吸收天然氧吧的气息，让人心旷神怡。小区由3层小住宅（别墅）和高层住宅组团形成，布局独具匠心，观感错落有致，其中：一期开发的3层小住宅（别墅）组团由88栋中西结合的别墅组成（36栋为稀缺独栋别墅，52栋为尊贵双拼别墅），共七种户型，建筑面积在247-383㎡区间，每一种户型都精雕细琢、不拘一格：全框架结构、平缓坡屋顶、朱红陶瓦、伸出挑屋檐、水平饰线条、成列玻璃窗、开放式底层、红黄陶砖墙面、深绿色窗框、古罗马圆形立柱、欧式的建筑线条，凹凸丰富的立面层次，蕴涵中国的古典浪漫情怀和欧式的奢华品质，凸显她雍容华贵、庄重典雅和与众不同的特有气派。</span></span></span><br/></p>', '2018-03-13 01:21:57.379000', '1', '1', '中兴金海湾', '北海市银海区中兴金海湾', '84', '0.00', '71251', '北海市中兴房地产开发有限公司', '3', '二手房', '1.6', '40%', null, '', '97', '', null, '北海亲和物业服务有限公司', '【1.0元/m²/月】【电梯费25元/月】', '871', '浙江东路8号（公务员小区东南面）', '3', '', '1', '2', '30', '21.442009', '109.153126', '', '', '2', null, '', '2', '2');
