INSERT INTO `aj_premise` VALUES ('94', '<p><span style=\"font-size: 18px;\">【项目简介】<span style=\"color: rgb(51, 51, 51); font-family: &#39;microsoft yahei&#39;; font-size: 14px; line-height: 25px; background-color: rgb(255, 255, 255);\">莱<span style=\"color: rgb(51, 51, 51); line-height: 25px; font-family: arial, helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\">茵名郡二期由北海鹏基实业有限公司投资1.5亿开发，莱茵名郡位于南京路交江苏路东南角，向北100米到新世纪大道，向东500米到广东路，往南依次是浙江路、杭州路、银滩大道、金海岸大道，昕景华庭交通完善方便，道路平地宽敞，可方便快速到达全城各个区域。 　　</span></span></span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 25px; font-family: arial, helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\"><br/></span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 25px; font-family: arial, helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\">莱茵名郡周边高端小区林立，附近有公务员小区、恒生嘉园、怡康春城、海岸华府、金域华府、世纪新苑、茗桂园、桐洋家园、美欧庭园、臻墅园等，而北海市新机关幼儿园就在莱茵名郡南侧不到80米的位置，甚至，在莱茵名郡和机关幼儿园中间，还有一个用地面积11162.65平方、绿化率为81.86%、已经建设好的大型社区公园。可以说，莱茵名郡拥有一流的居住环境品质。 莱茵名郡二期，两栋29层的三梯六户</span><br/></p><p><span style=\"color: rgb(51, 51, 51); line-height: 25px; font-family: arial, helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\"><br/></span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 25px; font-family: arial, helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\">【周边配套】</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 25px; font-family: arial, helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\">【公交线路】可乘坐12路、16路公交车到公务员小区站下车，再步行500米可到达小区。</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 25px; font-family: arial, helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\">【小区内部配套】游泳池、超市、餐饮、凉亭、健身设备、地下停车场。</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 25px; font-family: arial, helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\">【医疗】华侨医院、北海市侨港镇社区卫生服务中心、侨港医院，和平卫生所第二分店。</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 25px; font-family: arial, helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\">【教育】华侨中学、北海市银海区银滩中心小学、北海机关幼儿园，和平小学。</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 25px; font-family: arial, helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\">【餐饮娱乐】北海渔村风味美食、韩国料理来日方长、北海渔村、浪花东北风味茶馆、海龙养生靓汤、火锅千味涮、东北家常饺子馆、蜀咱家酒店、金港湾食街、东北四季家常菜馆，金桃源酒楼、全福茗茶。</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 25px; font-family: arial, helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\">【银行】建行、工行、中行、邮政，农村信用社。</span></p><p><span style=\"color: rgb(51, 51, 51); line-height: 25px; font-family: arial, helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\"><br/></span></p>', '2018-03-13 18:02:02.332000', '1', '1', '莱茵名郡', '北海市银海区莱茵名郡', '41', '0.00', '110000', '北海鹏基实业有限公司', null, '新房2018-050', '3.0', '31%', null, '', '800', '', null, '北海永昶物业有限公司', '12.元/m²/月', '724', '银海区南京路与江苏路交汇处', '2', '', '1', '2', '28', '21.445373', '109.139222', '改善住宅，经济住宅，投资地产', '', '2', null, '', '2', '2');
INSERT INTO `aj_premise` VALUES ('95', '<p><span style=\"font-size: 18px;\"><strong>【项目简介】</strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">嘉盛名都由五象房地产开发公司开发，占地167亩，总建筑面积约70万平米，共有19栋楼，其中17栋高楼（32-33层）住宅，1栋高层（22层）公寓楼和一栋超高层（36层，151米高），5648户，5万平米的商业配套，五层的大型商场和商铺组成，分六期开发。项目绿化率超过30%以上，是城市大型现代化居住社区。小区实行人车分流，机动车位4274个（分地下3553个和地上721个），非机动车位8070个。</span><br style=\"outline: none; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;; font-size: 14px; line-height: 33px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\"><br/></span></span></span></p><p><strong><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">【小区内部配套】</span></span></span></strong><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">小区内设有假山景观、休闲走廊、篮球场、网球场、游泳池等设施。</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\"><br/></span></span></span></p><p><strong><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">【项目周围配套】</span></span></span></strong><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">项目的西侧是新区规划中的大型综合农贸市场和在建的大润发超市、贵兴市场、机关幼儿园、和平小学，东侧是银海区政府和大学园区，北海第二人民医院新址，北海中学和新体育中心也落址于此。斜对面就是大江埠旅游风景区，看海而不近海。</span></span></span><br/></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\"><br/></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">【<strong>交通</strong>】公交路线：搭乘1路车到银海区政府站下，往新世纪大道西路走250米到项目处。</span></span></span></p>', '2018-03-13 18:15:40.707000', '1', '1', '嘉盛名都', '北海市银海区嘉盛名都营销中心', '167', '7100.00', '700000', '北海五象房地产开发有限公司', '19', '新房2018-051', '4.8', '30%', null, '', '4274', '', null, '北海伟业联行物业服务有限公司', '1.5元/m²/月', '5648', '银海区广东南路与新世纪大道交汇处', '2', '', '1', '2', '36', '21.453192', '109.144082', '', '', '2', null, '', '2', '2');
INSERT INTO `aj_premise` VALUES ('96', '<p><span style=\"font-size: 18px;\">【<strong>项目简介</strong>】<span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">恒宇·海岸华府坐落在广西北海市，中国西部地区沿海开放城市。项目恰处 北海银滩与市中心中间。</span><br style=\"outline: none; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;; font-size: 14px; line-height: 33px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">恒宇·海岸华府面南背北，前瞰浩瀚大海，背靠城市繁华，整个楼盘视野开阔。</span><br style=\"outline: none; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;; font-size: 14px; line-height: 33px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\"><br/></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">恒宇海岸华府以多层花园洋房与高层为主；“二峰一谷”盆地式规划格局；立面采用简约欧式建筑风格；全南北朝向户型，阳光泳池，40%绿化率，使小区形成多元化、多景区的社区公园。</span></span></span><br/></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\"><br/></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">【<strong>周边配套</strong>】</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">【<strong>交通</strong>】乘坐12路、16路、17路公交车到公务员小区站下车，小区就在公务员小区北面。</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">【<strong>小区内部配套</strong>】游泳池、社区公园、地下停车场。</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\"><br/></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">【<strong>楼层状况</strong>】两栋3层、五栋6层和七栋18层的建筑组成。</span></span></span></p>', '2018-03-13 22:46:54.863000', '1', '1', '恒宇海岸华府', '北海市银海区海岸华府', '155', '0.00', '171711', '北海恒宇置业有限公司', '20', '二手房', '1.66', '35%', null, '', null, '', null, '深圳市金地物业管理有限公司', '2.6元/m²/月', '1500', '银海区广东路156号（新世纪大道以南、广东路西侧）', '3', '', '1', '2', '28', '21.450121', '109.143639', '', '', '2', null, '', '2', '2');
INSERT INTO `aj_premise` VALUES ('97', '<p><span style=\"font-size: 18px;\">【<strong>项目简介</strong>】<span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">金域华府总占地40亩，总建面9.6万㎡，建筑密度22%，绿地率达60%，容积率2.5，可容纳总户数870户，由5幢高层及一幢步梯楼围合式组合而成。宽楼间距，宽达61米。</span><br style=\"outline: none; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;; font-size: 14px; line-height: 33px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\"><br/></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">金域华府项目建筑风格率先尝试将东南亚风情与巴洛克艺术融会贯通。移植东南亚名贵物种，还原皇家园林主题长廊，在项目内形成了香堤雅境，普吉水岸，芭提雅园林，东巴乐园，香花诗韵五大园林景区。</span><br style=\"outline: none; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;; font-size: 14px; line-height: 33px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\"><br/></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">金域华府地处广东南路，沿线汇聚公务员小区、廉政公园、大型购物广场、风情商业街、便民市场、北海市第二人民医院等生活配套。同时，周边配置有机关幼儿园、九中、大学园区等优势教育资源，金域华府项目旁紧挨着的，就是银滩一小所在地，子女的教育、生活的方方面面都可以在附近得到解决。此外，金域华府坐拥全方位立体交通体系，市内外出行交通便利，距北海市中心、国际客运码头、银滩都在5公里范围内，道路直通机场、高速。</span></span></span><br/></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\"><br/></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">【<strong>周边设施</strong>】交通路线：乘坐12路、17路公交车到和兴居委会站下同，沿西走1.6公里即到。</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">【<strong>小区内部配套</strong>】儿童娱乐场、中型社区广场及芭提雅风情泳池。</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 33px; background-color: rgb(255, 255, 255);\">【<strong>楼层状况</strong>】项目一期包括5号步梯楼及6号电梯花园洋房。其中6号楼是主推的楼栋，高17层，2梯6户，基础户型为2房2厅，面积为79--188m²。</span></span></span></p>', '2018-03-13 23:01:42.676000', '1', '1', '金域华府', '北海市银海区金域华府', '8.6', '0.00', '96489', '北海金域房地产开发有限公司', '7', '二手房', '2.5', '60%', null, '', '560', '', null, '北海祥居物业有限公司', '1.5元/m²/月', '870', '银海区广东路交江苏路以西（公务员小区对面）', '3', '', '1', '2', '30', '21.448912', '109.141055', '', '', '2', null, '', '2', '2');
INSERT INTO `aj_premise` VALUES ('98', '<p><span style=\"font-size: 18px;\">【<strong>项目简介</strong>】<span style=\"color: rgb(51, 51, 51); line-height: 33px; font-family: arial, helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\">海悦华府项目紧邻广东路和新世纪大道，地处城市发展规划的中心区。周边配套齐备、成熟、出行便利</span></span><br/></p>', '2018-03-13 23:11:21.691000', '1', '1', '海悦华府', '北海市银海区海悦华府', '80', '0.00', '250000', '广西中良诚信投资有限公司', '10', '二手房', '3.95', '31%', null, '', '1368', '', null, '北京乐邦福物业管理有限公司', '1.6元/m²/月', '2278', '银海区新世纪大道以南、广东路以东、江苏路以北', '3', '', '1', '2', '30', '21.451724', '109.150269', '', '', '2', null, '', '2', '2');
INSERT INTO `aj_premise` VALUES ('99', '<p><span style=\"font-size: 18px;\">【<strong>项目简介</strong>】<span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">德利海北海位于北海市海城区西北岸，海景大道与湖北大道的交汇处，是各地旅游大巴进出北海的必经之地，处于老城区核心商圈、主城区核心商圈、海湾新城三圈交汇的中心地段。主城区核心商圈是北海市现在及未来经济、政治、文化发展的核心区域，毗邻老城区核心商圈，周边的配套完善。</span><br style=\"outline: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft Yahei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\"><br/></span></span></span></p><p><strong><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">【项目】</span></span></span></strong><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">南临&quot;广西海景路&quot;北海海景大道南、北临海景大道、南至茶亭路、东西在湖北路与上海路之间，毗邻国家A4级景点“北海海底世界公园”、海滨公园及五星级香格里拉大饭店、富丽华大酒店，区域内交通便利，景观优美，环境幽雅。</span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\"><br/></span></span></span></p><p><strong><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">【该项目】</span></span></span></strong><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">拥有700多米长的海岸线，是北海市规划建设中北部海岸的高档商住区，被誉为“皇冠上璀璨的明珠”，在这里可以尽享一览无余的海景，亲身感受温暖的阳光、温柔的海浪、金色的沙滩和清新的空气。基于北部湾国家战略，面向中国-东盟大经济蓝图，德利海北海正以独特的商业地产理念和领先的建筑规划构思，为北部湾经济区的高档商业发展前景添上浓重的一笔 ！</span><br style=\"outline: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft Yahei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\"><br/></span></span></span></p><p><strong><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">【项目】</span></span></span></strong><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">占地面积93亩，总建筑面积约为271358.08㎡，总住宅面积27万㎡，65000㎡的商业（含5000㎡艺术剧场）9栋32层住宅，1栋酒店，容积率为3.5，小区的绿化率为住宅：30%；商业、娱乐：10% ，可以说我们整个小区的绿化面积是非常大的。小区由9栋蝶式楼围合而成，共有1668户，从楼体布局可以看出，所有楼栋都是南北横向蝶式分布，围合在小区四周，这样可以使各楼栋的楼间距达到化以保证一线海景。整个小区为三期开发，整体的交房时间在2017年底。</span></span></span><br/></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\"><br/></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">【<strong>交通</strong>】乘坐10路、15路公交车到上海路口站下车，再沿上海路往北走500米左右，再往右走200米到海景大道和湖北路交界就是该项目。<br/></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\"><br/></span></span></span></p><p><span style=\"font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 18px;\"><span style=\"font-family: arial, helvetica, sans-serif; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">【<strong>小区内部配套</strong>】商业街、地下大型超市、中央音乐设施、中央剧场、魅力影城、酒吧街、休闲养生会所等。</span></span></span></p><p><span style=\"font-size: 18px;\"><br/></span></p>', '2018-03-16 21:27:31.336000', '1', '1', '德利海北海', '北海市海城区德利海北海', '93', '8200.00', '271358', '广西德利投资集团有限公司', '10', ' 新房2018-052', '3.5', '30%', null, '', '2065', '/static/upload/image/20180316/1521199944695029540_wt.jpg|/static/upload/image/20180316/1521199987289033064_wt.jpg|/static/upload/image/20180316/1521199992539091321_wt.jpg|/static/upload/image/20180316/1521199996508070526_wt.jpg|/static/upload/image/20180316/1521200058508034437_wt.jpg|/static/upload/image/20180316/1521200061820023669_wt.jpg|/static/upload/image/20180316/1521200122164039047_wt.jpg|/static/upload/image/20180316/1521200130461069716_wt.jpg|/static/upload/image/20180316/1521200101539045530_wt.jpg|/static/upload/image/20180316/1521200077945095179_wt.jpg|/static/upload/image/20180316/1521200080930022975_wt.jpg|/static/upload/image/20180316/1521200083930063799_wt.jpg|/static/upload/image/20180316/1521200090555084273_wt.jpg|/static/upload/image/20180316/1521200108445055687_wt.jpg', null, '北海新都物业有限公司', '2.0元/m²/月', '1668', '海城区海景大道与湖北路交汇处（北部湾1号旁）', '6', '', '1', '1', '47', '21.503464', '109.154379', '商业街，地下商城，餐饮娱乐，音乐剧场，影城，酒吧街，休闲养生会所', '', '1', null, '', '1', '2');
